}

public edit DirPartyName editOrderAccountNameDS(boolean _set, DirPartyName _name)
{
    var name = custInvoiceTable.editOrderAccountName(_set, _name);

    if (_set)
    {
        // let the master DS know that we are dirty so that rereading reference datasources updates the name
        custInvoiceTable_ds.forceWrite();
        custInvoiceTable_ds.rereadReferenceDataSources();
    }

    return name;
}

