void modified()
{
    super();

    if (custInvoiceTable.OneTimeCustomer && !custInvoiceTable.OrderAccount)
    {
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OneTimeCustomer)).allowEdit(false);
        custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OneTimeCustomer)).skip(true);
    }

    if (custInvoiceTable.OneTimeCustomer)
    {
        orderAccount_Name.allowEdit(true);
        cvHeader_OrderAccountName.allowEdit(true);
    }
    else
    {
        orderAccount_Name.allowEdit(false);
        cvHeader_OrderAccountName.allowEdit(false);
    }

    if (combinedViewTabPage.isActivePage())
    {
        cvHeader_OrderAccountName.setFocus();
    }
    else if (headerViewTabPage.isActivePage())
    {
        orderAccount_Name.setFocus();
    }
}

void modified()
{
    super();

    custInvoiceTable.DirectDebitMandate = CustDirectDebitMandate::defaultAndDisplayMandate(
        custInvoiceTable.InvoiceAccount,
        custInvoiceTable.PaymMode,
        custInvoiceTable.DirectDebitMandate,
        true,
        custInvoiceTable_ds,
        fieldNum(CustInvoiceTable, DirectDebitMandate));

    // Make sure the header has been saved once it has an order account
    if (!custInvoiceTable.RecId &&
        (!isBillingClassificationEnabled || custInvoiceTable.CustBillingClassification))
    {
        custInvoiceTable.insert();

        // This force write ensures that the proper events (active, initValue, etc.) get fired on
        // the child datasources. It is necessary because the insert above prevents the Client
        // from knowing the record is "dirty". Without it, certain defaults will not be initialized
        // on the child datasources causing problems further along in the FTI entry process.
        custInvoiceTable_ds.forceWrite(true);
    }

    if (custInvoiceTable.InvoiceDate)
    {
        DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlHeader.parmActiveValueFilterDate(custInvoiceTable.InvoiceDate);
    }

    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OneTimeCustomer)).allowEdit(false);
    custInvoiceTable_ds.object(fieldNum(CustInvoiceTable, OneTimeCustomer)).skip(true);
    custInvoiceTable_ds.initInvoiceNumbering_LT();

    custInvoiceTable_ds.refresh();
    custInvoiceTable_ds.rereadReferenceDataSources();
    element.allowedit();
    element.setFieldsEditable();

    if (TaxIntegrationUtils::isTaxInformationEnabled() && custInvoiceTable.orig().OrderAccount != '')
    {
        CustTable custTable = CustTable::find(custInvoiceTable.OrderAccount);
        CustInvoiceLine custInvoiceLineLoc;
        while select custInvoiceLineLoc
            where custInvoiceLineLoc.ParentRecId == custInvoiceTable.RecId
        {
            TransTaxInformation transTaxInfo = TransTaxInformationHelper::findOrCreateTransTaxInformationByRecord(custInvoiceLineLoc);
            if (transTaxInfo)
            {
                ttsbegin;
                transTaxInfo.selectForUpdate(true);
                TransTaxInformationHelper::initFromCustVend(transTaxInfo, custTable);
                transTaxInfo.update();
                ttscommit;
            }
        }
    }

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.initWithTaxPayment_IN();
        element.initWouldYouClaimRefund_IN();
    }
}

