    if (!custInvoiceLine && CustInvoiceLine::exist(custInvoiceLine.ParentRecId, custInvoiceLine.LineNum))
    {
        custInvoiceLine.LineNum = CustInvoiceLine::lastLineNum_W(custInvoiceLine.ParentRecId) + 1;
    }

    reverseCharge.lineDSPreWrite();

    super();
}

