    localLogisticsPostalAddress = LogisticsPostalAddress::findRecId(custInvoiceTable.PostalAddress);
    TaxServiceCode_BR::lookup(_formControl, custInvoiceTable.ServiceCodeOnDlvAddress_BR,
                              localLogisticsPostalAddress.CountryRegionId,
                              localLogisticsPostalAddress.State,
                              localLogisticsPostalAddress.City);
}

public void init()
{
    super();

    var qbds = this.queryBuildDataSource();
    qbds.clearLinks();
    qbds.addLink(fieldNum(CustInvoiceTable, RecId), fieldNum(CustRelatedInvoice, CustInvoice));
    qbds.enabled(isConfigurationkeyEnabled(configurationKeyNum(FreeTextInvoiceCorrection)) || BrazilParameters::isEnabled());
    relatedInvoiceFilterRange = SysQuery::findOrCreateRange(qbds, fieldNum(CustRelatedInvoice, OriginalCustInvoice));
    relatedInvoiceFilterRange.status(RangeStatus::Hidden);
}

public void init()
{
    super();

    var qbds = this.queryBuildDataSource();
    qbds.addOrderByField(fieldNum(CustRelatedInvoice, RecId), SortOrder::Ascending);
    relatedInvoiceRange = SysQuery::findOrCreateRange(qbds, fieldNum(CustRelatedInvoice, OriginalCustInvoice));
    relatedInvoiceRange.status(RangeStatus::Hidden);

    this.cacheAddMethod(tableMethodStr(CustRelatedInvoice, displayInvoiceDate));
    this.cacheAddMethod(tableMethodStr(CustRelatedInvoice, displayInvoiceAmount));
    this.cacheAddMethod(tableMethodStr(CustRelatedInvoice, displayInvoiceId));
}

public void executeQuery()
{
    relatedInvoiceRange.value(queryValue(custRelatedInvoice.OriginalCustInvoice));

    super();
}

public void init()
{
    if (this.isNeeded())
    {
        this.linkType(FormLinkType::OuterJoin);
        this.optionalRecordMode(OptionalRecordMode::None);
    }
    super();
}

