/// <summary>
/// Refresh the form for recently added corrected invoice.
/// </summary>
/// <param name="_formRun">
/// The object of dialog form.
/// </param>
public void dialogClosed(xFormRun _formRun)
{
    CustICorrectedInvoiceParameters correctedInvoiceParms;
    CustFreeInvoiceCorrectionPost custFreeInvoiceCorrectionPost;
    CustInvoiceTable adjustCustInvoiceTable;
    CustInvoiceTable lookupCustInvoiceTable, correctedCustInvoiceTable;

    super(_formRun);

    if (_formRun.closedOk())
    {
        lookupCustInvoiceTable = custInvoiceTable;

        correctedInvoiceParms = _formRun as CustICorrectedInvoiceParameters;

        if (correctedInvoiceParms)
        {
            custInvoiceTable.CorrectionReasonCode = correctedInvoiceParms.parmReasonCode();
            custInvoiceTable.update();

            correctedCustInvoiceTable = CustInvoiceTable::findRecId(correctedInvoiceParms.parmCorrectedInvoiceRecId());

            if (correctedCustInvoiceTable)
            {
                lookupCustInvoiceTable = correctedCustInvoiceTable;
            }
        }

        element.args().lookupRecord(lookupCustInvoiceTable);
        custInvoiceTable_ds.research(false);

        if (correctedCustInvoiceTable)
        {
            if (countryRegion_EEU)
            {
                if (correctedInvoiceParms.parmCreateCancellingInvoiceOnly())
                {
                    adjustCustInvoiceTable = CustInvoiceTable::findRecId(correctedInvoiceParms.parmCorrectedInvoiceRecId());

                    custFreeInvoiceCorrectionPost = CustFreeInvoiceCorrectionPost::newCorrectedInvoice(adjustCustInvoiceTable);

                    custFreeInvoiceCorrectionPost.post();

                    custInvoiceTable_ds.research(true);
                }
                else
                {
                    element.putInEditMode();
                }
            }
            else
            {
                element.putInEditMode();
            }
        }
    }
}

void clicked()
{
    filterRelatedInvoices = true;

    custInvoiceTable_ds.executeQuery();

    this.enabled(false);
    allInvoice.enabled(true);
}

void clicked()
{
