    return isModified;
}

/// <summary>
/// Perform a lookup for the <c>BillingClassification</c> field of the <c>CustBillingClassification</c> table.
/// </summary>
public void lookup()
{
    CustBillingClassification::custBillingClassificationLookup(this);
}

/// <summary>
/// filter invoices by selected <c>BillingClassification</c> value.
/// </summary>
/// <returns>
/// The value from a call to super is returned.
/// </returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        element.filterBillingClassification(this.text());
    }
    return ret;
}

[Hookable(false)]
public boolean modified()
{
    boolean isModified = super();

    if (isFTIPerformanceFeatureEnabled && isModified)
    {
        this.selectionChanged();
        custInvoiceTable_ds.executeQuery();
    }

    return isModified;
}

[Hookable(false)]
public void selectionChanged()
{
    postedCriteria = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name()).rangeField(fieldNum(CustInvoiceTable, Posted));
    if (postedCriteria)
    {
        postedCriteria.value(this.valueAllOpenPosted());
    }
}

private str valueAllOpenPosted()
{
    switch (allOpenPostedField.selection())
    {
        case AllOpenPosted::All : return SysQuery::valueUnlimited();
        case AllOpenPosted::Open : return enum2Value(NoYes::No);
        case AllOpenPosted::Posted : return enum2Value(NoYes::Yes);

        default: return SysQuery::valueUnlimited();
    }
}

[Hookable(false)]
public boolean modified()
{
    boolean isModified = super();

    if (isFTIPerformanceFeatureEnabled && isModified)
    {
        this.selectionChanged();
        custInvoiceTable_ds.executeQuery();
    }

    return isModified;
}

[Hookable(false)]
public void selectionChanged()
{
    date currentDate = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());
    queryBuildDataSource queryDs  = CustInvoiceTable_ds.query().dataSourceName(CustInvoiceTable_ds.name());
    invoiceDateCriteria = queryDs.rangeField(fieldNum(CustInvoiceTable, InvoiceDate));

    if (invoiceDateCriteria)
    {
        date startDate = this.valueCustInvoiceDateRange(currentDate);

        if (startDate)
        {
            invoiceDateCriteria.value(SysQuery::range(startDate, currentDate));
        }
        else
        {
            invoiceDateCriteria.value('');
        }
    }
}

[Hookable(false)]
public void setValue()
{
    FilterInvoiceDate.selection(CustInvoiceDateRange::ThirtyDays);
}

[Hookable(false)]
public void setValueForInquiryForm()
{
    FilterInvoiceDate.selection(CustInvoiceDateRange::Custom);
}

private date valueCustInvoiceDateRange(date _currentDate)
{
    switch (FilterInvoiceDate.selection())
    {
        case CustInvoiceDateRange::ThirtyDays : return _currentDate - 30;
        case CustInvoiceDateRange::SixtyDays : return _currentDate - 60;
        case CustInvoiceDateRange::NinetyDays : return _currentDate - 90;
        case CustInvoiceDateRange::Custom : return dateNull();

        default: return dateNull();
    }
}

public boolean modified()
{
    DimensionDefault dimensionDefault;
    boolean ret;

    ret = super();

    if (custInvoiceLine)
    {
        if (custInvoiceLine.DefaultDimension)
        {
            dimensionDefault = custInvoiceLine.DefaultDimension;
        }
        else
        {
            dimensionDefault = custInvoiceTable.DefaultDimension;
        }

        // update the default dimension on the line
        custInvoiceLine.DefaultDimension = dimensionDefault;
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if (mcrCallCenterEnabled)
    {
        custInvoiceTable_OrderAccount.setFocus();
    }
    return ret;
}
