[Form]
public class CustHierarchy extends FormRun
{
    private List nodeGridGroupControls;
    private CustHierarchyId selectedCustHierarchyId;
    private Version selectedCustHierarchyVersion;
    private boolean isMultipleCatalogsFeatureEnabled;
    private Catalog callerCatalog;
    private CustHierarchy callerCustHierarchy;

}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    // Verify if Multiple Catalogs feature is enabled
    isMultipleCatalogsFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ChannelMultipleCatalogsFeature::instance());

    // Form called from Catalog form
    if (isMultipleCatalogsFeatureEnabled && element.args().caller() && element.args().caller().name() == formStr(RetailCatalogDetails))
    {
        // Get callers Catalog record
        callerCatalog = element.args().record();
    }
    else
    {
        // Form called with custHierarchy context
        if (element.args().caller())
        {
            // Get callers Catalog record
            callerCustHierarchy = element.args().record();
        }
    }

    super();

    this.initializeFormElements();
}

