    return CustHierarchy.CustHierarchyId;
}

/// <summary>
/// Return currently selected node customers.
/// </summary>
/// <returns>selected cust hierarchy nodes.</returns>
public List getSelectedNodeCustomers()
{
    FormDataSource selectedNodes = FormDataUtil::getFormDataSource(CustHierarchyNode);
    List selectedCustomersParty = new List (Types::Int64);
    for (CustHierarchyNode selectedCustHierarchyNode = selectedNodes.getFirst(true) ? selectedNodes.getFirst(true) : selectedNodes.cursor();
        selectedCustHierarchyNode;
        selectedCustHierarchyNode = selectedNodes.getNext())
    {
        selectedCustomersParty.addEnd(selectedCustHierarchyNode.Party);
    }
    return selectedCustomersParty;
}

