/// <summary>
/// Initializes the form elements.
/// </summary>
private void initializeFormElements()
{
    CatalogsTabPage.visible(isMultipleCatalogsFeatureEnabled ? true : false);
    this.initializeNodeGridPropertyGroupFilter();

    element.updateAndExecuteCustHierarchyCatalogQuery();
}

/// <summary>
/// Initializes the node grid property group filter.
/// </summary>
private void initializeNodeGridPropertyGroupFilter()
{
    ListEnumerator enumerator;
    FormGroupControl groupControl;

    nodeGridGroupControls = new List(Types::Class);
    nodeGridGroupControls.addEnd(NodeGridGroup_General);
    nodeGridGroupControls.addEnd(NodeGridGroup_MiscellaneousDetails);
    nodeGridGroupControls.addEnd(NodeGridGroup_SalesDemographics);
    nodeGridGroupControls.addEnd(NodeGridGroup_CreditAndCollections);
    nodeGridGroupControls.addEnd(NodeGridGroup_SalesOrderDefaults);
    nodeGridGroupControls.addEnd(NodeGridGroup_PaymentDefaults);
    nodeGridGroupControls.addEnd(NodeGridGroup_InvoiceAndDelivery);
    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        nodeGridGroupControls.addEnd(NodeGridGroup_Retail);
    }

    enumerator = nodeGridGroupControls.getEnumerator();

    while (enumerator.moveNext())
    {
        groupControl = enumerator.current();
        NodeGridPropertyGroupFilter.add(groupControl.caption());
    }

    enumerator.reset();
    enumerator.moveNext();
    groupControl = enumerator.current();

    this.refreshNodeGridPropertyGroups(groupControl.caption());
}

/// <summary>
/// Displays selected node grid property group.
/// </summary>
/// <param name = "_selectedItemText">The caption of node grid property group control.</param>
private void refreshNodeGridPropertyGroups(str _selectedItemText)
{
    ListEnumerator enumerator;
    FormGroupControl groupControl;

    enumerator = nodeGridGroupControls.getEnumerator();

    while (enumerator.moveNext())
    {
        groupControl = enumerator.current();
        groupControl.visible(groupControl.caption() == _selectedItemText);

        // Need to explicitly set FormComboBoxControl visibility to avoid null reference exception on the UI.
        NodeGrid_InvoicingAndDeliveryOnHold.visible(NodeGridGroup_CreditAndCollections.visible());
        NodeGrid_AllocationPriority.visible(NodeGridGroup_SalesOrderDefaults.visible());
        NodeGrid_CustHierarchyCatalogRefRecId.visible(false);
        NodeGrid_CustHierarchyCatalogId.visible(isMultipleCatalogsFeatureEnabled);
    }
}

/// <summary>
/// Returns selected property group control of the node grid.
/// </summary>
/// <returns><c>FormGroupControl</c> of the node grid.</returns>
private FormGroupControl getActiveNodeGridPropertyGroup()
{
    ListEnumerator enumerator;
    FormGroupControl groupControl;

    enumerator = nodeGridGroupControls.getEnumerator();

    while (enumerator.moveNext())
    {
        groupControl = enumerator.current();

        if (groupControl.visible())
        {
            return groupControl;
        }
    }

    return null;
}

private void updateAndExecuteCustHierarchyNodeQuery(CustHierarchyVersion _version)
{
    Query q = CustHierarchyNode_ds.query();
    QueryBuildDataSource qbds = q.dataSourceTable(tableNum(CustHierarchyNode));

    qbds.clearRanges();
    qbds.addRange(fieldNum(CustHierarchyNode, CustHierarchyId)).value(queryValue(CustHierarchy.CustHierarchyId));
    qbds.addRange(fieldNum(CustHierarchyNode, VersionAdded)).value(
    strFmt('((%3 >= %1) && ((%2 == 0) || (%1 > %2)))',
    fieldStr(CustHierarchyNode, VersionAdded),
    fieldStr(CustHierarchyNode, VersionRemoved),
    _version.Version)
    );
    CustHierarchyNode_ds.executeQuery();
}

private void updateAndExecuteCustHierarchyCatalogQuery()
{
    // Query for assigned customer hierarchies
    Query qCustHierarchy =  CustHierarchy_ds.query();
    QueryBuildDataSource qbdsCustHierarchy = qCustHierarchy.dataSourceTable(tableNum(CustHierarchy));

    // When opened from the RetailCatalogDetails or CustTable forms, a filter is applied  to this form to show
    // only customer hierarchies in the context of the catalog or the customer, respectively.
    if (callerCustHierarchy.RecId != 0)
    {
        qbdsCustHierarchy.addRange(fieldNum(CustHierarchy, CustHierarchyId)).value(queryValue(callerCustHierarchy.CustHierarchyId));
    }

    qbdsCustHierarchy.addSortField(fieldNum(CustHierarchy, CustHierarchyId));
    QueryBuildDataSource qbdsCustHierarchyCatalogJoin = qbdsCustHierarchy.addDataSource(tableNum(CustHierarchyCatalog));
    qbdsCustHierarchyCatalogJoin.relations(false);
    qbdsCustHierarchyCatalogJoin.fields().dynamic(NoYes::Yes);
    qbdsCustHierarchyCatalogJoin.addLink(fieldNum(CustHierarchy, CustHierarchyId), fieldNum(CustHierarchyCatalog, CustHierarchyId));
    qbdsCustHierarchyCatalogJoin.addRange(fieldNum(CustHierarchyCatalog, CatalogRefRecId)).value(queryValue(callerCatalog.RecId));

    // Form called from Catalog form must filter by catalog
    if (isMultipleCatalogsFeatureEnabled && callerCatalog.RecId != 0)
    {
        qbdsCustHierarchyCatalogJoin.joinMode(JoinMode::InnerJoin);
    }
    else
    {
        qbdsCustHierarchyCatalogJoin.joinMode(JoinMode::OuterJoin);
    }

    CustHierarchy_ds.executeQuery();
    CustHierarchyCatalog_ds.executeQuery();
}

internal void refreshQueries()
{
    CustHierarchyVersion version = CustHierarchyVersion::getActiveVersion(CustHierarchy.CustHierarchyId);

    if (element.selectedCustHierarchyId != CustHierarchy.CustHierarchyId || element.selectedCustHierarchyVersion != version.Version)
    {
        element.updateAndExecuteCustHierarchyNodeQuery(version);

        element.selectedCustHierarchyId = CustHierarchy.CustHierarchyId;
        element.selectedCustHierarchyVersion = version.Version;
    }

}

