/// <summary>
/// Initializes new <c>CustHierarchyNode</c> record.
/// </summary>
public void initValue()
{
    super();

    int64 nodeCount = CustHierarchyNode::getActiveNodesCount(CustHierarchy.CustHierarchyId);

    CustHierarchyNode.CustHierarchyId = CustHierarchy.CustHierarchyId;
    CustHierarchyNode.Type = CustHierarchyNodeType::Customer;
    CustHierarchyNode.Role = nodeCount ? CustHierarchyNodeRole::User : CustHierarchyNodeRole::Admin;
    CustHierarchyNode.VersionAdded = CustHierarchyVersion::getActiveVersion(CustHierarchy.CustHierarchyId).Version;
}

