/// <summary>
/// Create promoting business partner prospect and navigate to it.
/// </summary>
public void clicked()
{
    super();

    if (CustHierarchy.SellerChannel != '')
    {
        error('@Retail:CustHierarchyIsAlreadySellerErrorMessage');
        return;
    }

    ttsbegin;
    smmBusRelTypeGroup::insertB2BProspect();
    ttscommit;

    smmBusRelTable prospect;

    select firstonly prospect where prospect.Party == CustHierarchy.OrganizationParty;

    if (!prospect)
    {
        if (Box::yesNo(strFmt('@Retail:PromoteBusinessPartnerConfirmationMessage'), DialogButton::No) == DialogButton::Yes)
        {
            RetailBusinessPartnerPromoteBusinessPartnerProcessor::createPromoteBusinessPartnerProspect(CustHierarchy, custTable.DataAreaId, 0);
            select firstonly prospect where prospect.Party == CustHierarchy.OrganizationParty;
        }
        else
        {
            return;
        }
    }

    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(smmBusRelTable), MenuItemType::Display);

    Args args = new Args(menuFunction.object());
    args.caller(this);

    args.record(prospect);

    menuFunction.run(args);
}

