        locArgs.caller();
        locArgs.record(locCatalog);
        locArgs.lookupRecord(locCatalog);
        locArgs.formViewOption(FormViewOption::Details);

        new MenuFunction(menuitemDisplayStr(RetailCatalogs), MenuItemType::Display).run(locArgs);
    }
}

