private int64 getAmmountOfAdminsSelected(FormDataSource _selectedNodes)
{
    FormDataUtil::getFormDataSource(CustHierarchyNode);

    int64 adminsFound = 0;
    for (CustHierarchyNode selectedCustHierarchyNode = _selectedNodes.getFirst(true) ? _selectedNodes.getFirst(true) : _selectedNodes.cursor();
        selectedCustHierarchyNode;
        selectedCustHierarchyNode = _selectedNodes.getNext())
    {
        if (selectedCustHierarchyNode.Role == CustHierarchyNodeRole::Admin)
        {
            adminsFound++;
        }
    }

    return adminsFound;
}

private List<RecId> getSelectedCustomersRecIds()
{
    List selectedRecIds = new List(Types::Int64);

    FormDataSource selectedNodes = FormDataUtil::getFormDataSource(CustHierarchyNode);

    for (CustHierarchyNode selectedCustHierarchyNode = selectedNodes.getFirst(true) ? selectedNodes.getFirst(true) : selectedNodes.cursor();
        selectedCustHierarchyNode;
        selectedCustHierarchyNode = selectedNodes.getNext())
    {
        if (selectedCustHierarchyNode.Role == CustHierarchyNodeRole::User)
        {
            selectedRecIds.addEnd(CustTable::findByPartyRecId(selectedCustHierarchyNode.Party).RecId);
        }
    }

    return selectedRecIds;
}

private RecId getSelectedAdminRecId()
{
    FormDataSource selectedNodes = FormDataUtil::getFormDataSource(CustHierarchyNode);

    for (CustHierarchyNode selectedCustHierarchyNode = selectedNodes.getFirst(true) ? selectedNodes.getFirst(true) : selectedNodes.cursor();
        selectedCustHierarchyNode;
        selectedCustHierarchyNode = selectedNodes.getNext())
    {
        if (selectedCustHierarchyNode.Role == CustHierarchyNodeRole::Admin)
        {
            CustTable adminCustTable = CustTable::findByPartyRecId(selectedCustHierarchyNode.Party);;
            return adminCustTable.RecId;
        }
    }

    return 0;
}

/// <summary>
/// Overrides the attributes in a given property group for all the users in the hierarchy by copying data from the admin.
/// </summary>
public void clicked()
{
    FormGroupControl nodeGridPropertyGroup = element.getActiveNodeGridPropertyGroup();
    FormDataSource selectedNodes = FormDataUtil::getFormDataSource(CustHierarchyNode);
    int64 adminsAmmount = this.getAmmountOfAdminsSelected(selectedNodes);


    if (adminsAmmount >= 2)
    {
        warning("@CustHierarchy:ChangeRoleTwoOrMoreAdminsSelected");
    }
    else if (adminsAmmount < 1)
    {
        warning("@CustHierarchy:ChangeRoleNoAdminSelected");
    }
    else
    {
        if (box::yesNo('@CustHierarchy:OverrideConfirmationMessage', dialogButton::No) == dialogButton::Yes)
        {
            CustHierarchyPropertyGroup propertyGroup;

            switch (nodeGridPropertyGroup)
            {
                case NodeGridGroup_General:
                    propertyGroup = CustHierarchyPropertyGroup::General;
                    break;
                case NodeGridGroup_MiscellaneousDetails:
                    propertyGroup = CustHierarchyPropertyGroup::MiscellaneousDetails;
                    break;
                case NodeGridGroup_SalesDemographics:
                    propertyGroup = CustHierarchyPropertyGroup::SalesDemographics;
                    break;
                case NodeGridGroup_CreditAndCollections:
                    propertyGroup = CustHierarchyPropertyGroup::CreditAndCollections;
                    break;
                case NodeGridGroup_SalesOrderDefaults:
                    propertyGroup = CustHierarchyPropertyGroup::SalesOrderDefaults;
                    break;
                case NodeGridGroup_PaymentDefaults:
                    propertyGroup = CustHierarchyPropertyGroup::PaymentDefaults;
                    break;
                case NodeGridGroup_InvoiceAndDelivery:
                    propertyGroup = CustHierarchyPropertyGroup::InvoiceAndDelivery;
                    break;
                case NodeGridGroup_Retail:
                    propertyGroup = CustHierarchyPropertyGroup::Retail;
                    break;
            }

            List selectedCustomersRecId = this.getSelectedCustomersRecIds();

            RecId selectedAdmin = this.getSelectedAdminRecId();

            CustHierarchyUpdateHelper::overrideCustHierarchyAttributesForUsers(CustHierarchy.CustHierarchyId, propertyGroup, selectedCustomersRecId, selectedAdmin);

            CustHierarchyNode_ds.research(true);
        }
    }
}

