/// <summary>
/// Lookup <c>Catalog</c> table.
/// </summary>
public void lookup()
{
    Query                   query;
    QueryBuildDataSource    qbdsCatalog, qbdsCustHierarchy, qbdsCustHierarchyCatalog;
    SysTableLookup sysTableLookup;
    str languageId = SystemParameters::getSystemLanguageId();

    sysTableLookup = SysTableLookup::newParameters(tableNum(RetailCatalogView), this);

    query = new Query();
    qbdsCatalog = query.addDataSource(tableNum(RetailCatalogView));
    qbdsCatalog.addRange(fieldNum(RetailCatalogView, Language)).value(queryValue(languageId));
    qbdsCatalog.addRange(fieldNum(RetailCatalogView, CatalogTarget)).value(queryValue(RetailCatalogTarget::B2B));
    qbdsCatalog.addSortField(fieldNum(RetailCatalogView, CatalogId));

    qbdsCustHierarchyCatalog = qbdsCatalog.addDataSource(tableNum(CustHierarchyCatalog));
    qbdsCustHierarchyCatalog.relations(false);
    qbdsCustHierarchyCatalog.fields().dynamic(NoYes::Yes);
    qbdsCustHierarchyCatalog.addLink(fieldNum(RetailCatalogView, RecId), fieldNum(CustHierarchyCatalog, CatalogRefRecId));
    qbdsCustHierarchyCatalog.joinMode(JoinMode::InnerJoin);

    qbdsCustHierarchy = qbdsCustHierarchyCatalog.addDataSource(tableNum(CustHierarchy));
    qbdsCustHierarchy.relations(false);
    qbdsCustHierarchy.fields().dynamic(NoYes::Yes);
    qbdsCustHierarchy.addLink(fieldNum(CustHierarchyCatalog, CustHierarchyId), fieldNum(CustHierarchy, CustHierarchyId));
    qbdsCustHierarchy.joinMode(JoinMode::InnerJoin);
    qbdsCustHierarchy.addRange(fieldNum(CustHierarchy, CustHierarchyId)).value(queryValue(CustHierarchy.CustHierarchyId));

    sysTableLookup.addLookupfield(fieldNum(RetailCatalogView, CatalogId));
    sysTableLookup.addLookupfield(fieldNum(RetailCatalogView, FriendlyName));
    sysTableLookup.addLookupfield(fieldNum(RetailCatalogView, State));
    sysTableLookup.addLookupfield(fieldNum(RetailCatalogView, ValidFrom));
    sysTableLookup.addLookupfield(fieldNum(RetailCatalogView, ValidTo));
    sysTableLookup.addSelectionfield(fieldNum(RetailCatalogView, CatalogId));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Execute code when the CatalogId field is modified in the grid.
/// </summary>
/// <returns>True when the field is modified. False otherwise.</returns>
public boolean modified()
{
    boolean ret;

    CustHierarchyNode.CustHierarchyCatalogRefRecId = CustHierarchyCatalog::FindByCatalogId(NodeGrid_CustHierarchyCatalogId.text()).RecId;
    ret = super();
    CustHierarchyNode_ds.research(true);

    return ret;
}

/// <summary>
/// Execute code when the retail channel id field is modified in the grid.
/// </summary>
/// <returns>True when the field is modified. False otherwise.</returns>
public boolean modified()
{
    boolean ret;

    CustHierarchyRetailChannel.Channel = RetailChannels_RetailChannelId.text();
    ret = super();
    CustHierarchyRetailChannel_ds.research();

    return ret;
}

/// <summary>
/// Lookup <c>RetailOnlineChannelTable</c> table.
/// </summary>
public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbdsCustHierarchyRetailChannel;
    QueryBuildDataSource qbdsRetailOnlineChannelNameViewTable;
    QueryBuildDataSource qbdsOmOperatingUnit;
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(RetailOnlineChannelNameView), this);

    qbdsRetailOnlineChannelNameViewTable = query.addDataSource(tableNum(RetailOnlineChannelNameView));
    qbdsRetailOnlineChannelNameViewTable.addSortField(fieldNum(RetailOnlineChannelNameView, RetailChannelId));
    qbdsRetailOnlineChannelNameViewTable.addRange(fieldNum(RetailOnlineChannelNameView, CustomerType)).value(enum2Value(OnlineStoreCustomerType::B2B));

    qbdsCustHierarchyRetailChannel = qbdsRetailOnlineChannelNameViewTable.addDataSource(tableNum(CustHierarchyRetailChannel));
    qbdsCustHierarchyRetailChannel.relations(false);
    qbdsCustHierarchyRetailChannel.fields().dynamic(NoYes::No);
    qbdsCustHierarchyRetailChannel.addLink(fieldNum(CustHierarchyRetailChannel, Channel), fieldNum(RetailOnlineChannelNameView, RetailChannelId));
    qbdsCustHierarchyRetailChannel.addRange(fieldNum(CustHierarchyRetailChannel, CustHierarchyId)).value(queryValue(CustHierarchy.CustHierarchyId));
    qbdsCustHierarchyRetailChannel.joinMode(JoinMode::NoExistsJoin);

    sysTableLookup.addLookupfield(fieldNum(RetailOnlineChannelNameView, RetailChannelId));
    sysTableLookup.addLookupfield(fieldNum(RetailOnlineChannelNameView, Name));
    sysTableLookup.addSelectionfield(fieldNum(RetailOnlineChannelNameView, RetailChannelId));
    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Enable jumping to Online Stores form.
/// </summary>
public void jumpRef()
{
    Args args = new Args();

    RetailOnlineChannelTable currentRetailOnlineChannelTable = RetailOnlineChannelTable::findByRetailChannelId(CustHierarchy.SellerChannel);
    args.record(currentRetailOnlineChannelTable);
    args.lookupRecord(currentRetailOnlineChannelTable);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailSPOnlineStoreDetailPage), MenuItemType::Display);
    args.caller(element);
    menuFunction.run(args);
}
