[Form]
public class CustInterestJournal extends FormRun
{
    Map sumInterestAmountFeeCache;

}

void init()
{
    super();

    if (element.args().record())
    {
        accountNum.visible(false);
    }

    sumInterestAmountFeeCache = new Map(Types::String, Types::Real);

    this.updateDesign();

    custInterestJour_ds.object(fieldNum(CustInterestJour, CustBillingClassification)).visible(CustParameters::isBillingClassificationEnabled());
}

public void updateDesign()
{
    JournalVoucher.enabled(CustInterestJour.Status == PrintPostCancel::Posted);
    JournalLineVoucher.enabled(CustInterestJour.Status == PrintPostCancel::Posted);
    Transactions.enabled(CustInterestJour.Status == PrintPostCancel::Posted);
    InterestFeeNoteEdit.visible(custInterestJour.Status == PrintPostCancel::Created);
    InterestFeeNoteView.visible(custInterestJour.Status != PrintPostCancel::Created);
    InterestTransNoteEdit.visible(custInterestJour.Status == PrintPostCancel::Created);
    InterestTransNoteView.visible(custInterestJour.Status != PrintPostCancel::Created);
    InterestTransLineNoteEdit.visible(custInterestJour.Status == PrintPostCancel::Created);
    InterestTransLineNoteView.visible(custInterestJour.Status != PrintPostCancel::Created);
}

public int active()
{
    int activeOK;

    activeOK = super();

    this.object(fieldNum(CustInterestJour, CustBillingClassification)).visible(CustParameters::isBillingClassificationEnabled());

    element.updateDesign();

    return activeOK;
}

public void cursorNotify(int _event)
{
    CustInterestJour interestJourCache;
    Set setInterestNote;

    super(_event);

    if (_event == 0 || _event == 1)
    {
        interestJourCache = custInterestJour_DS.getFirst(0,false);
        while (interestJourCache)
        {
            if (! sumInterestAmountFeeCache.exists(interestJourCache.InterestNote))
            {
                if (! setInterestNote)
                {
                    setInterestNote = new Set(Types::String);
                }
                setInterestNote.add(interestJourCache.InterestNote);
            }

            interestJourCache = custInterestJour_DS.getNext();
        }

        if (setInterestNote)
        {
            sumInterestAmountFeeCache = Map::create(CustInterestJour::sumInterestAmountFeeDisplayCache(setInterestNote.pack(),sumInterestAmountFeeCache.pack()));
        }
    }
}

void clicked()
{
    Args args = new Args(this);

    args.parmEnumType(enumNum(AllChosen));
    args.record(custInterestJour);
    args.parmEnum(AllChosen::All);

    this.menufunction().run(args);
}

public boolean modified()
{
    var isModified = super();

    if (isModified)
    {
        CustInterestJour::addStatusFilterToFormDatasource(custInterestJour_ds, this.selection());

        // Ensure the grid filter reflects the same value
        filterStatusGrid.selection(this.selection());
    }

    return isModified;
}

public boolean modified()
{
    var isModified = super();

    if (isModified)
    {
        CustInterestJour::addPrintedFilterToFormDatasource(custInterestJour_ds, this.selection());

        // Ensure the nav pane filter reflects the same value
        filterPrintedGrid.selection(this.selection());
    }

    return isModified;
}

