    [Form]
    public class CustInterestNote extends FormRun
{
}

public void close()
{
    Object callerForm;

    callerForm = element.args().caller();

    if (callerForm && callerForm.name() == formStr(CustOpenInvoicesListPage))
    {
        callerForm.doRefresh();
    }

    super();
}

public void init()
{
    super();

    if (element.args() && element.args().lookupTable() == tableNum(CustInterestJour) && element.args().lookupField() && element.args().lookupValue())
    {
        if (element.args().lookupField() == fieldNum(CustInterestJour, Status))
        {
            PrintPostCancel status;
            status = str2Enum(status, element.args().lookupValue());
            switch (status)
            {
                case PrintPostCancel::Canceled:
                    filterStatusGrid.selection(PrintPostCancelFilter::Canceled);
                    break;
                case PrintPostCancel::Created:
                    filterStatusGrid.selection(PrintPostCancelFilter::Created);
                    break;
                case PrintPostCancel::Posted:
                    filterStatusGrid.selection(PrintPostCancelFilter::Posted);
                    break;
                default:
                    filterStatusGrid.selection(PrintPostCancelFilter::All);
                    break;
            }

            filterStatusGrid.modified();

            SysQuery::findOrCreateRange(custInterestJour_ds.queryBuildDataSource(), element.args().lookupField()).value(SysQuery::value(status));
        }
        else if (element.args().lookupField() == fieldNum(CustInterestJour, Printed))
        {
            NoYes printed;
            printed = str2Enum(printed, element.args().lookupValue());
            switch (printed)
            {
                case NoYes::Yes:
                    filterPrintedGrid.selection(PrintedStatusFilter::Printed);
                    break;
                case NoYes::No:
                    filterPrintedGrid.selection(PrintedStatusFilter::NotPrinted);
                    break;
                default:
                    filterPrintedGrid.selection(PrintedStatusFilter::All);
                    break;
            }

            filterPrintedGrid.modified();

            SysQuery::findOrCreateRange(custInterestJour_ds.queryBuildDataSource(), element.args().lookupField()).value(SysQuery::value(printed));
        }
    }

    if (element.args() && element.args().record() && element.args().record().TableId == tableNum(CustTable))
    {
        CustTable custTable = element.args().record();
        SysQuery::findOrCreateRange(custInterestJour_ds.queryBuildDataSource(), fieldNum(CustInterestJour, AccountNum)).value(SysQuery::value(custTable.AccountNum));
    }

    element.updateDesign();
}

public void updateDesign()
{
    // Only enable the post/print buttons when at least one interest transaction is marked.
    if (custInterestJour.isAtLeastOneInterestTransactionIncluded())
    {
        custReport_interestNote.enabled(true);
        postInterest.enabled(custInterestJour.Status == PrintPostCancel::Created);
    }
    else
    {
        custReport_interestNote.enabled(false);
        postInterest.enabled(false);
    }

    cancelInterest.enabled(custInterestJour.Status == PrintPostCancel::Created);
    ledgerTransactVoucher.enabled(custInterestJour.Status == PrintPostCancel::Posted);
    TransactionVoucherButton.enabled(custInterestJour.Status == PrintPostCancel::Posted);
    InterestFeeNoteEdit.visible(CustInterestJour.Status == PrintPostCancel::Created);
    InterestFeeNoteView.visible(CustInterestJour.Status != PrintPostCancel::Created);
    InterestTransNoteEdit.visible(custInterestJour.Status == PrintPostCancel::Created);
    InterestTransNoteView.visible(custInterestJour.Status != PrintPostCancel::Created);
    InterestTransLineNoteEdit.visible(custInterestJour.Status == PrintPostCancel::Created);
    InterestTransLineNoteView.visible(custInterestJour.Status != PrintPostCancel::Created);
}

/// <summary>
/// Checks for <c>InterestCalcAccountChoice</c> value
/// </summary>
/// <returns>
/// true if InterestCalcAccountChoice is of type transaction or account; otherwise, false.
/// </returns>
protected boolean checkInterestCalcAccountChoice()
{
    return (custInterestJour.InterestCalcAccountChoice == InterestCalcAccountChoice::Transaction ||
            custInterestJour.InterestCalcAccountChoice == InterestCalcAccountChoice::Account) ? true : false;
}

int active()
{
    int ret;

    ret = super();

    this.object(fieldNum(CustInterestJour, CustBillingClassification)).visible(CustParameters::isBillingClassificationEnabled());

    element.updateDesign();

    if (custInterestJour.Updated || custInterestJour.Canceled)
    {
        custInterestJour_ds.allowEdit(false);
        custInterestTrans_ds.allowEdit(false);
        logisticsLocationHeader_ds.allowEdit(false);
        logisticsPostalAddressHeader_ds.allowEdit(false);
        DimensionEntryControl.enabled(false);
    }
    else
    {
        custInterestJour_ds.allowEdit(true);
        custInterestTrans_ds.allowEdit(true);
        logisticsLocationHeader_ds.allowEdit(true);
        logisticsPostalAddressHeader_ds.allowEdit(true);
        DimensionEntryControl.enabled(true);
    }

    if (element.checkInterestCalcAccountChoice())
    {
        interestJournal_PostingProfile.allowEdit(false);
    }
    else
    {
        interestJournal_PostingProfile.allowEdit(true);
    }

    return ret;
}

public void write(boolean _flag = false)
{
    if (_flag)
    {
        super();
    }
    else
    {
        logisticsPostalAddressHeader_ds.write();
    }

    logisticsLocationHeader_ds.forceWrite(false);
}

public boolean validate()
{
    boolean ret;
    LogisticsLocation location;
    ret = super();
    if (ret)
    {
        if (!logisticsLocationHeader.RecId ||
            logisticsLocationHeader.LocationId != LogisticsLocation::find(logisticsLocationHeader.RecId).LocationId
           )
        {
            location = LogisticsLocation::findByLocationId(logisticsLocationHeader.LocationId);
            if (location)
            {
                // Cannot use existing location.
                ret = checkFailed("@SYS136233");
            }
        }
    }

    return ret;
}

public void modified()
{
    LogisticsLocation location;
    LogisticsPostalAddress postalAddress;

    if (!logisticsLocationHeader.RecId ||
        logisticsLocationHeader.LocationId != LogisticsLocation::find(logisticsLocationHeader.RecId).LocationId
       )
    {
        // Do you want to add a new location?
        if (!logisticsLocationHeader.LocationId || !custInterestJour.PostalAddress || Box::yesNo("@SYS136234",DialogButton::Ok) == DialogButton::Yes)
        {
            location.LocationId = logisticsLocationHeader.LocationId;
            location.Description = logisticsLocationHeader.Description;
            logisticsLocationHeader.data(location);
            super();
            logisticsPostalAddressHeader.data(postalAddress);
        }
        else
        {
            super();
        }
    }
    else
    {
        super();
    }
}

public void write()
{
    ttsbegin;

    logisticsLocationHeader_ds.write(true);

    logisticsPostalAddressHeader.Location = logisticsLocationHeader.RecId;
    super();

    custInterestJour.PostalAddress = logisticsPostalAddressHeader.recid;
    custInterestJour_ds.write();
    ttscommit;
}

