public boolean modified()
{
    var isModified = super();

    if (isModified)
    {
        CustInterestJour::addStatusFilterToFormDatasource(custInterestJour_ds, this.selection());

        // Ensure the grid filter reflects the same value
        filterStatusGrid.selection(this.selection());
    }

    return isModified;
}

