private void updatetDesignEntryCertificate_W()
{
    CustEntryCertificateJour_W entryCertificateJour;
    CustEntryCertificateInvoiceLink_W entryCertificateInvoiceLink;

    select firstonly RecId from entryCertificateJour
        where entryCertificateJour.SourceRefTableId == custInvoiceJour.TableId
            && entryCertificateJour.SourceRefRecId == custInvoiceJour.RecId;

    CustEntryCertificateJourIssued_W.enabled(entryCertificateJour.RecId);

    select firstonly RecId from entryCertificateJour
        exists join entryCertificateInvoiceLink
        where entryCertificateInvoiceLink.EntryCertificateJourRefRecId == entryCertificateJour.RecId
            && entryCertificateInvoiceLink.InvoiceRefRecId == custInvoiceJour.RecId;

    CustEntryCertificateJourReceived_W.enabled(entryCertificateJour.RecId);

    CustEntryCertificateCreate_W.enabled(custInvoiceJour_W.EntryCertificateReceiptStatus_W == EntryCertificateReceiptStatus_W::Required);
}

