private boolean validateInterCompanyInvoiceCanBePosted()
{
    boolean ret = true;

    if (custInvoiceJour.InterCompanyCompanyId
        && CustInvoiceJourValidatePostInterCompanyInvoiceFlight::instance().isEnabled())
    {
        changecompany(custInvoiceJour.InterCompanyCompanyId)
        {
            VendInvoicePurchLink vendInvoicePurchLink;

            select firstonly RecId from vendInvoicePurchLink
                where vendInvoicePurchLink.OrigPurchId == custInvoiceJour.InterCompanyPurchId
                   && vendInvoicePurchLink.InvoiceId == custInvoiceJour.InvoiceId;

            if (vendInvoicePurchLink.RecId)
            {
                ret = checkFailed(strFmt("@SCM:IntercompanyInvoiceCannotBeGeneratedBecauseInvoiceWithNumberExists",
                    custInvoiceJour.InterCompanyCompanyId, custInvoiceJour.InvoiceId));
            }
        }
    }

    if (ret && InterCompanyCheckInvoicePostingAccessFlight::instance().isEnabled())
    {
        ret = custInvoiceJour.salesTable().hasRightsPostInterCompanyPurchaseOrder(false);
    }

    return ret;
}

