public int active()
{
    int ret;
    RefRecId localAccountingEventRec;
    // <GBR>
    boolean isInvoicePrintAvailable;
    EFDocument_BR eFiscalDocument;
    EFDocState_BR efDocState;
    // </GBR>

    ret = super();

    localAccountingEventRec = custInvoiceJour.custTrans().AccountingEvent;

    if (localAccountingEventRec)
    {
        localAccountingEventRec = custInvoiceJour.ReversedRecId == 0;
    }

    boolean enableIntercompany = (custInvoiceJour.InterCompanyCompanyId ? true : false);
    interCompanyTransferInventDim.enabled(enableIntercompany);
    buttonInterCompanyUpdate.enabled(enableIntercompany);
    subLedgerJournalLine_Overview.enabled(localAccountingEventRec ? true : false);
    accountingDistributionsDocumentView.enabled(localAccountingEventRec ? true : false);
    CustEntryCertificateMenuButton_W.enabled(custInvoiceJour.RefNum == RefNum::SalesOrder);

    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]))
    {
        fiscalDocChange_PL.enabled(custInvoiceJour_PL.FiscalDocState_PL == PlFiscalDocState::FiscalDocument ||
                                   custInvoiceJour_PL.FiscalDocState_PL == PlFiscalDocState::PrintedFiscalDocument);
    }
    // </GEEPL>

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        qtyDelivered_RU.visible(custInvoiceJour_RU.InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute);
        qtyDeliveryReturned_RU.visible(custInvoiceJour_RU.InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute);
        qtyToDelivery_RU.visible(custInvoiceJour_RU.InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute);

        goodsInRoute_RU.enabled(custInvoiceJour_RU.InvoicePostingType_RU == SalesInvoicePostingType_RU::GoodsInRoute &&
                                custInvoiceJour.Qty > 0);
    }
    // </GEERU>

    // <GBR>
    if (BrazilParameters::isEnabled() &&
        activeFiscalDocument.RefRecId != custInvoiceJour.RecId)
    {
        activeFiscalDocument = FiscalDocument_BR::findByRef(custInvoiceJour.TableId, custInvoiceJour.RecId);
        isInvoicePrintAvailable = true;

        if (activeFiscalDocument.isElectronic())
        {
            eFiscalDocument = EFDocument_BR::findByRef(activeFiscalDocument.RecId);
            efDocState = EFDocState_BR::createFromEFdocument(eFiscalDocument);
            isInvoicePrintAvailable = efDocState.canPrintDanfe() || efDocState.canPrintContingencyDanfe();
        }

        element.control(element.controlId(formControlStr(CustInvoiceJournal, SalesInvoiceCopy))).enabled(isInvoicePrintAvailable);
        element.control(element.controlId(formControlStr(CustInvoiceJournal, SalesInvoiceOriginal))).enabled(isInvoicePrintAvailable);
        element.control(element.controlId(formControlStr(CustInvoiceJournal, SalesInvoice))).enabled(isInvoicePrintAvailable);
    }
    // </GBR>

    // <GEEHU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        if (custInvoiceJour_W.TaxReimbursementDoc_HU)
        {
            custInvoiceTrans_W_ds.object(fieldNum(CustInvoiceTrans_W, TaxReimbursement_HU)).allowEdit(false);
            taxReimbursementHeader_HU.allowEdit(false);
            taxReimbursementHeader_HU_Grid.allowEdit(false);
            vatSettledButton_HU.enabled(!custInvoiceJour_W.VatSettled_HU);
        }
        else
        {
            custInvoiceTrans_W_ds.object(fieldNum(CustInvoiceTrans_W, TaxReimbursement_HU)).allowEdit(true);
            taxReimbursementHeader_HU.allowEdit(true);
            taxReimbursementHeader_HU_Grid.allowEdit(true);
            vatSettledButton_HU.enabled(false);
        }

        if (custInvoiceJour.taxReimbursement_HU())
        {
            printReimbursement_HU.enabled(true);
            taxReimburstmentSlipOriginal_HU.enabled(true);
        }
        else
        {
            printReimbursement_HU.enabled(false);
            taxReimburstmentSlipOriginal_HU.enabled(false);
        }

        custInvoiceTrans_ds.refresh();
        custInvoiceJour_ds.refresh();
    }
    // </GEEHU>
    // <GIN>
    if (TaxParameters::isExciseEnable_IN() && custInvoiceJour.salesTable().isExcisable_IN())
    {
        ExciseSalesInvoiceReport.enabled(true);
    }
    // </GIN>

    element.updatetDesignEntryCertificate_W();

    return ret;
}

public void init()
{
    QueryBuildDataSource queryDataSourceLink;
    // <GIN>
    CustInvoiceJour custInvoiceJourLoc;
    SalesTable salesTableLoc;
    // </GIN>
    TAMDeduction tamDeduction;

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        custInvoiceJour_ds.cacheAddMethod(tableMethodStr(CustInvoiceJour, billingClassification));
    }

    if (element.args().dataset() == tableNum(SalesTable))
    {
        this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(fieldNum(CustInvoiceJour, RefNum)).value(queryValue(RefNum::SalesOrder));
        this.query().dataSourceTable(tableNum(CustInvoiceJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustInvoiceJour)).addDataSource(tableNum(CustInvoiceSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(CustInvoiceSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldNum(SalesTable, SalesId));
        // <GIN>
        if (isMatchedPackingSlipInquiryEnabled)
        {
            salesTableLoc = element.args().record();

            custInvoiceJourLoc = CustInvoiceJour::findFromSalesId_IN(salesTableLoc.SalesId);

            if (custInvoiceJourLoc)
            {
                conPackingSlipIdDate = custInvoiceTrans.packingSlipIdAndDate(custInvoiceJourLoc);
            }
            else
            {
                conPackingSlipIdDate = CustInvoiceTrans::packingSlipIdAndDateBySalesId(custInvoiceTrans,
                    salesTableLoc.SalesId);
            }

            conInventDeliverQty = CustPackingSlipTrans::inventAndDeliverQty(salesTableLoc.SalesId);
        }
        // </GIN>
    }

    if (element.args().dataset() == tableNum(CustInvoiceTrans) && 
        (element.args().caller() && element.args().caller().name() == formStr(InventPackagingMaterialCreateTrans)))
    {
        this.query().dataSourceTable(tableNum(CustInvoiceJour)).clearDynalinks();

        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustInvoiceJour)).addDataSource(tableNum(CustInvoiceSalesLink));
        queryDataSourceLink.relations(true);
        queryDataSourceLink.addDynalink(fieldNum(CustInvoiceSalesLink, OrigSalesId),
                                        element.args().record(),
                                        fieldNum(CustInvoiceTrans, SalesId));
        queryDataSourceLink.addDynalink(fieldNum(CustInvoiceSalesLink, InvoiceId),
                                        element.args().record(),
                                        fieldNum(CustInvoiceTrans, InvoiceId));
        queryDataSourceLink.addDynalink(fieldNum(CustInvoiceSalesLink, InvoiceDate),
                                        element.args().record(),
                                        fieldNum(CustInvoiceTrans, InvoiceDate));
    }

    if (element.args().dataset() == tableNum(RetailStatementJour))
    {
        this.query().dataSourceTable(tableNum(CustInvoiceJour)).clearLinks();

        // Link SalesOrderId field in RetailTransactionTable with SalesId filed in CustInvoiceJour; then scope
        // RetailTransactionTable with StatementId=RetailStatementJour.StatementId
        queryDataSourceLink = this.query().dataSourceTable(tableNum(CustInvoiceJour)).addDataSource(tableNum(RetailTransactionTable));
        queryDataSourceLink.joinMode(JoinMode::ExistsJoin);
        queryDataSourceLink.addLink(fieldNum(CustInvoiceJour, SalesId), fieldNum(RetailTransactionTable, SalesOrderId));
        queryDataSourceLink.addRange(fieldNum(RetailTransactionTable, StatementId)).value(element.args().record().(fieldNum(RetailStatementJour, StatementId)));
        queryDataSourceLink.addRange(fieldNum(RetailTransactionTable, Type)).value(enum2str(RetailTransactionType::Sales));
    }

    if (element.args().dataset() == tableNum(CustPrepaymentInvoiceTable))
    {
        CustPrepaymentInvoiceTable CustPrepaymentInvoiceTable = element.args().record();

        if (CustPrepaymentInvoiceTable)
        {
            this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(fieldNum(CustInvoiceJour, InvoiceId)).value(CustPrepaymentInvoiceTable.InvoiceId);
            this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(fieldNum(CustInvoiceJour, LedgerVoucher)).value(CustPrepaymentInvoiceTable.Voucher);
        }
    }

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        if (factureSource)
        {
            this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(fieldNum(CustInvoiceJour, RecId)).value(factureSource.jourRange());
        }//
        else // Called via jumpRef -->
        {
            if (element.args() && element.args().caller() && (element.args().caller() is FormRun))
            {
                this.query().dataSourceTable(tableNum(CustInvoiceJour)).clearSortIndex();
                this.query().dataSourceTable(tableNum(CustInvoiceJour)).addSortIndex(indexNum(CustInvoiceJour, InvoiceNumIdx));
            }//
        }// Called via jumpRef <--
    }
    // </GEERU>

    // <GEEHU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]))
    {
        this.cacheAddMethod(tableMethodStr(CustInvoiceJour, taxReimbursement_HU));
    }
    // </GEEHU>
    if (element.args().dataset() == tableNum(TAMDeduction)
        && element.args().record().(fieldNum(TAMDeduction, AccountNum)))
    {
        tamDeduction = element.args().record();
        this.query().dataSourceTable(tablenum(CustInvoiceJour)).clearDynalinks();
        this.query().dataSourceTable(tableNum(CustInvoiceJour)).addRange(
            fieldNum(CustInvoiceJour, InvoiceAccount)).value(tamDeduction.AccountNum);
    }
}

