public void linkActive()
{
    QueryBuildRange qbrReversedRecId;

    qbrReversedRecId = this.query().dataSourceTable(tableNum(CustInvoiceTrans)).findRange(fieldNum(CustInvoiceTrans, ReversedRecId));

    if (!qbrReversedRecId)
    {
        qbrReversedRecId = this.query().dataSourceTable(tableNum(CustInvoiceTrans)).addRange(fieldNum(CustInvoiceTrans, ReversedRecId));
    }

    if (custInvoiceJour.ReversedRecId == 0)
    {
        qbrReversedRecId.value(queryValue('0'));
    }
    else
    {
        qbrReversedRecId.value(queryValue('!0'));
    }

    super();
}

