/// <summary>
///    returns PackingSlipId of the <c>CustPackingSlipTrans</c> table.
/// </summary>
/// <param name="_custInvoiceTrans">
///    The <c>CustInvoiceTrans</c> buffer.
/// </param>
/// <returns>
/// PackingSlipId of the <c>CustPackingSlipTrans</c> table.
/// </returns>
public display PackingSlipId packingSlipId(CustInvoiceTrans _custInvoiceTrans)
{
    int pos;

    pos = conFind(conPackingSlipIdDate, _custInvoiceTrans.RecId);
    return conPeek(conPackingSlipIdDate, pos + 1);
}

/// <summary>
///    returns delivery date of the <c>CustPackingSlipTrans</c> table.
/// </summary>
/// <param name="_custInvoiceTrans">
///    The <c>CustInvoiceTrans</c> buffer.
/// </param>
/// <returns>
/// Delivery date of the <c>CustPackingSlipTrans</c> table.
/// </returns>
public display SalesShippingDate deliveryDate(CustInvoiceTrans _custInvoiceTrans)
{
    int pos;

    pos = conFind(conPackingSlipIdDate, _custInvoiceTrans.RecId);
    return conPeek(conPackingSlipIdDate, pos + 2);
}

public void init()
{
    if (isMatchedPackingSlipInquiryEnabled)
    {
        custPackingSlipTrans_ds.autoQuery(true);
    }
    super();
}

/// <summary>
///    returns SalesQty of the <c>SalesParmSubLine</c> table.
/// </summary>
/// <param name="_custPackingSlipTrans">
///    The <c>CustPackingSlipTrans</c> buffer.
/// </param>
/// <returns>
/// SalesQty value <c>CustPackingSlipTrans</c> table.
/// </returns>
public display SalesQty salesQuantity(CustPackingSlipTrans _custPackingSlipTrans)
{
    int pos;

    pos = conFind(conInventDeliverQty, _custPackingSlipTrans.RecId);
    return conPeek(conInventDeliverQty, pos + 2);
}

/// <summary>
///    returns InventQty of the <c>SalesParmSubLine</c> table.
/// </summary>
/// <param name="_custPackingSlipTrans">
///    The <c>CustPackingSlipTrans</c> buffer.
/// </param>
/// <returns>
/// SalesQty value <c>CustPackingSlipTrans</c> table.
/// </returns>
public display InventQty inventQuantity(CustPackingSlipTrans _custPackingSlipTrans)
{
    int pos;

    pos = conFind(conInventDeliverQty, _custPackingSlipTrans.RecId);
    return conPeek(conInventDeliverQty, pos + 1);
}

public void init()
{
    QueryBuildDataSource qbds;
    DictRelation dr;

    super();

    // <GEEU>
    if (countryRegion_EEU)
    {
        qbds = origCustInvoiceJour_DS.query().dataSourceTable(tableNum(CustInvoiceJour));
        qbds.clearDynalinks();
        qbds.clearLinks();

        qbds = qbds.addDataSource(tableNum(CustInvoiceTrans));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.fetchMode(QueryFetchMode::One2One);
        qbds.relations(true);

        qbds = qbds.addDataSource(tableNum(CustInvoiceTrans_W));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.fetchMode(QueryFetchMode::One2One);
        qbds.addLink(fieldNum(CustInvoiceTrans, RecId), fieldNum(CustInvoiceTrans_W, RefReturnInvoiceTrans_W));
        qbds.addRange(fieldNum(CustInvoiceTrans_W, RefReturnInvoiceTrans_W)).value(SysQuery::valueNot(0));

        qbds = qbds.addDataSource(tableNum(CustInvoiceTrans));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.fetchMode(QueryFetchMode::One2One);
        dr = new DictRelation(tableNum(CustInvoiceTrans_W));
        dr.loadNameRelation(literalStr(CustInvoiceTrans));
        qbds.addRelation(dr);

        qbds = qbds.addDataSource(tableNum(CustInvoiceJour));
        qbds.joinMode(JoinMode::ExistsJoin);
        qbds.fetchMode(QueryFetchMode::One2One);
        qbds.relations(true);

        qbds.addDynalink(fieldNum(CustInvoiceJour, RecId), custInvoiceJour, fieldNum(CustInvoiceJour, RecId));
    }
    // </GEEU>
}

