edit PrintOverdueFineTxt_FR editPrintOverdueFineTxt_FR(boolean _set,
                                                       CustInvoiceJour_FR _custInvoiceJourFR,
                                                       PrintOverdueFineTxt_FR _print)
{
    if (_set)
    {
        if (! _custInvoiceJourFR.RecId)
        {
            _custInvoiceJourFR = custInvoiceJour.custInvoiceJour_FR();
        }

        _custInvoiceJourFR.PrintOverdueFineTxt = _print;
        custInvoiceJour.packCustInvoiceJour_FR(_custInvoiceJourFR);

        custInvoiceJour_FR_ds.write();
        custInvoiceJour_FR_ds.reread();
        custInvoiceJour_FR_ds.refresh();

        custInvoiceJour_ds.write();
    }

    return _custInvoiceJourFR.PrintOverdueFineTxt;
}

