void clicked()
{
    CustInvoiceJour custInvoiceJourLocal;
    Args args = new Args();
    // <GTH>
    boolean isUnrealizedVATEnabled = TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled();
    // </GTH>

    //    args.caller(this); Does this have to be set?
    for (custInvoiceJourLocal = getFirstSelection(custInvoiceJour_ds); custInvoiceJourLocal; custInvoiceJourLocal = custInvoiceJour_ds.getNext())
    {
        args.record(custInvoiceJourLocal);

        // <GTH>
        if (isUnrealizedVATEnabled && custInvoiceJourLocal.custInvoiceJourExtensionTH().NoteFormat != TaxUnrealizedSalesNoteFormat::None)
        {
            new MenuFunction(menuitemOutputStr(CustDebitCreditNoteCopy), MenuItemType::Output).run(args);
        }
        else
        {
            // </GTH>
            if (custInvoiceJourLocal.SalesId)
            {
                // Sales order invoice
                new MenuFunction(menuitemOutputStr(SalesInvoiceCopy), MenuItemType::Output).run(args);
            }
            else
            {
                // Free text invoice
                new MenuFunction(menuitemOutputStr(FreeTextInvoiceCopy), MenuItemType::Output).run(args);
            }
            // <GTH>
        }
        // </GTH>
    }

    super();
}

void clicked()
{
    CustInvoiceJour custInvoiceJourLocal;
    Args args = new Args();
    RecordSortedList journalList = SalesInvoiceJournalPrint::construct().newJournalList();
    ReprintOriginals_IS reprintOriginals;
    // <GTH>
    boolean isUnrealizedVATEnabled = TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled();
    // </GTH>
    #ISOCountryRegionCodes

    reprintOriginals = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIS]) ? ReprintOriginals_IS::Prompt : ReprintOriginals_IS::Always;

    //    args.caller(this); Does this have to be set?
    for (custInvoiceJourLocal = getFirstSelection(custInvoiceJour_ds); custInvoiceJourLocal; custInvoiceJourLocal = custInvoiceJour_ds.getNext())
    {
        if (reprintOriginals == ReprintOriginals_IS::Prompt &&
            CustInvoiceJour::findRecId(custInvoiceJourLocal.RecId).PrintedOriginals &&
            Box::yesNo(strFmt("@SYS125573",
                              custInvoiceJourLocal.InvoiceId,
                              custInvoiceJourLocal.InvoiceDate),
                       DialogButton::No) == DialogButton::No)
            continue;

        journalList.ins(custInvoiceJourLocal);
    }

    journalList.first(custInvoiceJourLocal);
    while (custInvoiceJourLocal)
    {
        args.record(custInvoiceJourLocal);

        // <GTH>
        if (isUnrealizedVATEnabled && custInvoiceJourLocal.custInvoiceJourExtensionTH().NoteFormat != TaxUnrealizedSalesNoteFormat::None)
        {
            new MenuFunction(menuitemOutputStr(CustDebitCreditNote), MenuItemType::Output).run(args);
        }
        else
        {
            // </GTH>
            if (custInvoiceJourLocal.SalesId)
            {
                // Sales order invoice
                new MenuFunction(menuitemOutputStr(SalesInvoiceOriginal), MenuItemType::Output).run(args);
            }
            else
            {
                // Free text invoice
                new MenuFunction(menuitemOutputStr(FreeTextInvoiceOriginal), MenuItemType::Output).run(args);
            }
            // <GTH>
        }
        // </GTH>

        if (! journalList.next(custInvoiceJourLocal))
            break;
    }

    super();
}

