public void pageActivated()
{
    super();

    if (lineTaxReimbursementChanged)
    {
        lineTaxReimbursementChanged = false;
        custInvoiceTrans_W_ds.write();
        custInvoiceJour_ds.cacheCalculateMethod(tableMethodStr(CustInvoiceJour, taxReimbursement_HU));

        if (custInvoiceJour.taxReimbursement_HU())
        {
            printReimbursement_HU.enabled(true);
            taxReimburstmentSlipOriginal_HU.enabled(true);
        }
        else
        {
            printReimbursement_HU.enabled(false);
            taxReimburstmentSlipOriginal_HU.enabled(false);
        }
    }
}

