        public void init()
{
    QueryBuildDataSource qBDS;
    QueryBuildRange qBR;

    super();
    
    qBDS = CustinvoiceJour_DS.query().dataSourceTable(tablenum(CustinvoiceJour));
    qBR = qBDS.addRange(fieldnum(CustinvoiceJour, LedgerVoucher));
    qBR.value(custTrans.Voucher);
}

        public edit boolean editMark(boolean _set, CustinvoiceTrans _custinvoiceTrans, boolean _mark)
{
    if (_set)
    {
        if (!_mark)
        {
            if (g_Marked.exists(_custinvoiceTrans.RecId))
            {
                g_Marked.remove(_custinvoiceTrans.RecId);
            }
        }
        else
        {
            g_Marked.insert(_custinvoiceTrans.RecId, _custinvoiceTrans.TableId);
        }
    }

    return g_Marked.exists(_custinvoiceTrans.RecId);
}

        void clicked()
{
    CustInvoiceTrans m_CustInvoiceTrans;
    AmountCur       sumofTotal = 0;
    AmountCur       sumofLineAmount = 0;
    CustTransOpen   custTransOpenLoc;
    real            settledLinePercent = 0;
    MapEnumerator   m_mapEnumerator;

    super();

    m_mapEnumerator = g_marked.getEnumerator();
    while (m_mapEnumerator.moveNext())
    {
        m_CustInvoiceTrans = CustInvoiceTrans::findRecId(m_mapEnumerator.currentKey());
        sumofLineAmount += m_CustInvoiceTrans.LineAmount;
        sumofTotal += abs(m_CustInvoiceTrans.getIndirectTax());
    }

    if (custInvoiceJour.SalesBalance !=0 )
    {
        settledLinePercent = (sumofLineAmount*1.0)/custInvoiceJour.SalesBalance;

        // update settled amount
        ttsbegin;
        update_recordSet custTransOpenLoc
            setting settledTotalAmount_IN = sumofTotal + sumofLineAmount,
                    settledLinePercent_IN = settledLinePercent
            where custTransOpenLoc.AccountNum   == custTrans.AccountNum
                && custTransOpenLoc.RefRecId    == custTrans.RecId;
        ttscommit;
    }
}
