public boolean canClose()
{
    return super();
}

public boolean canShowInputDialog()
{
    boolean canShow = false;
    CustBillingCodeVersion custBillingCodeVersion;
    CustBillingCodeCustomField custBillingCodeCustomField;

    custBillingCodeVersion = CustBillingCodeVersion::findByDate(tmplLine_CustInvoiceStandardLineTemplate.BillingCode, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));

    select firstonly RecId from custBillingCodeCustomField
                        where custBillingCodeCustomField.CustBillingCodeVersion == custBillingCodeVersion.RecId
                            && custBillingCodeCustomField.IsActive == true;

    if (custBillingCodeCustomField.RecId)
    {
        canShow = true;
    }

    return canShow;
}

/// <summary>
/// This method is called by a <c>CustCustomFieldInputDialog</c> instance when it is closed.
/// </summary>
/// <param name="_mapCustomFieldElements">
/// A <c>Map</c> containg a list of <c>CustCustomFieldElement</c> instances.
/// </param>
/// <param name="_okClicked">
/// A true value if the ok button was clicked otherwise a false value.
/// </param>
/// <remarks>
/// This method resets all the defaults that were set by selecting a billing code if cancel is clicked and the billing code was empty.
/// </remarks>
public void closeCustomFieldDialog(Map _mapCustomFieldElements, boolean _okClicked)
{
    if (_okClicked)
    {
        mapCustCustomFieldElements = _mapCustomFieldElements;
    }

    customFieldDialogOkClicked = _okClicked;
    brnCustomField.enabled(true);
}

/// <summary>
/// Creates a record in the specific data source.
/// </summary>
/// <param name="_formDataSourceName">
/// Name of the data source where the record will be created.
/// </param>
/// <param name="_append">
/// A Boolean that indicates whether to insert the record after or before the current
/// cursor position. If true, the new record is inserted after the current record; otherwise
/// before the current record.
/// </param>
public void createRecord(str _formDataSourceName, boolean _append = false)
{
    Map selectedFormDataSources = new Map(Types::String, Types::String);

    if (_formDataSourceName == custInvoiceTemplate_ds.name())
    {
        super(_formDataSourceName, _append);
    }
    else
    // <GIN>
    if (_formDataSourceName == tmplLine_ds.name())
    // </GIN>
    {
        selectedFormDataSources.insert(tmplLine_ds.name(), tableStr(CustInvoiceStandardLineTemplate));
        tmplLine_ds.createTypes(selectedFormDataSources, true);
    }
    // <GIN>
    else if (_formDataSourceName == custInvoiceTemplateTaxExtension_IN_ds.name())
    {
        selectedFormDataSources.insert(custInvoiceTemplateTaxExtension_IN_ds.name(), tableStr(CustInvoiceTemplateTaxExtension_IN));
        custInvoiceTemplateTaxExtension_IN_ds.createTypes(selectedFormDataSources);
    }
    else if (_formDataSourceName == custInvoiceLineTemplateTaxExtension_IN_ds.name())
    {
        selectedFormDataSources.insert(custInvoiceLineTemplateTaxExtension_IN_ds.name(), tableStr(CustInvoiceLineTemplateTaxExtension_IN));
        custInvoiceLineTemplateTaxExtension_IN_ds.createTypes(selectedFormDataSources);
    }
    // </GIN>
}

/// <summary>
/// Initializes the <c>CustInvoiceTemplate</c> form.
/// </summary>
public void init()
{
    // <GIN>
    #ISOCountryRegionCodes
    // </GIN>

    isBillingClassificationEnabled = CustParameters::isBillingClassificationEnabled();

    super();

    custTemplateLine_LedgerAccount.parmPostingType(LedgerPostingType::CustRevenue);

    custParameters = CustParameters::find();

    //<GMX>
    electronicInvoice_MX.visible(EInvoiceCFDIParameters_MX::isElectronicInvoiceEnabled());
    //</GMX>
    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisible_IN();
        element.setTaxWithholdVisible_IN();
    }
    // </GIN>

    custInvoiceTemplate_ds.object(fieldNum(CustInvoiceTemplate,
                                        BillingClassification)).visible(isBillingClassificationEnabled);
    CustomFieldButtonGroup.visible(isBillingClassificationEnabled);

    TmplLine_CustInvStdLineTmp_BillCode_PSN.visible(isBillingClassificationEnabled);

    TmplLine_CustInvoiceStandardLineTemplate_AmountDetails.visible(isBillingClassificationEnabled);

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::FTI))
    {
        CustInvoiceTemplateLine_OverrideSalesTax.visible(true);
        CustInvoiceTemplate_ds.object(fieldNum(CustInvoiceTemplate, OverrideSalesTax)).visible(true);
    }

    boolean areFinTagsEnabled = this.areFinTagsEnabled();

    if (!areFinTagsEnabled)
    {
        TabFinTagHeader.visible(areFinTagsEnabled);
    }
}

private boolean areFinTagsEnabled()
{
    return isFinTagConfigurationSetForCurrent
        && AccountsReceivableFinTagFlight::instance().isEnabled();
}

public boolean openCustomField()
{
    boolean canShow = false;
    MenuFunction menuFunction;

    Args args;

    canShow = element.canShowInputDialog();
    if (canShow)
    {
        args = new Args();
        args.parm('0');
        args.record(tmplLine_CustInvoiceStandardLineTemplate);
        args.parmEnumType(CustCustomFieldSourceType::CustInvoiceTemplateLine);
        args.parmObject(mapCustCustomFieldElements);
        args.caller(element);

        menuFunction = new MenuFunction(menuitemActionStr(CustCustomFieldInputDialog),MenuItemType::Action);
        brnCustomField.enabled(false);
        menuFunction.run(args);
    }
    else
    {
        customFieldDialogOkClicked = true;
    }
    customFieldDialog = null;

    return customFieldDialogOkClicked;
}

public CustCustomFieldInputDialog parmCustomFieldDialog(CustCustomFieldInputDialog _customFieldDialog = customFieldDialog)
{
    customFieldDialog = _customFieldDialog;

    return customFieldDialog;
}

