public void setFieldAccess()
{
    boolean isProjectSelected;

    isProjectSelected = tmplLine_CustInvoiceStandardLineTemplate.ProjId != '';

    tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, ProjCategoryId)).mandatory(isProjectSelected);

    if (isBillingClassificationEnabled)
    {
        if (tmplLine_CustInvoiceStandardLineTemplate.BillingCode)
        {
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).mandatory(false);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).allowEdit(false);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, AmountDetails)).allowEdit(false);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LineTxt)).allowEdit(false);
            custInvoiceDistributionTemplateButton.enabled(false);
        }
        else
        {
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).mandatory(true);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).allowEdit(true);
            tmplline_ds.object(fieldnum(CustInvoiceLineTemplate, TaxGroup)).allowEdit(true);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, AmountDetails)).allowEdit(true);
            tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LineTxt)).allowEdit(true);
            custInvoiceDistributionTemplateButton.enabled(tmplLine_CustInvoiceStandardLineTemplate.AmountCur != 0);

            // Make Ledger account field editable based on project settings
            if (isProjectSelected && !custParameters.ProjLedgerAllowEdit && custParameters.EnableProject)
            {
                tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).allowEdit(false);
            }
            else
            {
                tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, LedgerDimension)).allowEdit(true);
            }
        }

        //
        // Set the edit mode for some controls based on if it is a billing code line or not:
        // Controls will be read-only if it is a billing code line.
        // Controls will be editable if it is not a billing code line.
        //
        boolean isControlEditableByBillingCode = !tmplLine_CustInvoiceStandardLineTemplate.BillingCode;

        CustInvoiceTemplateLine_CustInvoiceStandardLineTemplate_Description.allowEdit(isControlEditableByBillingCode);
        CustInvoiceTemplateLine_TaxItemGroup.allowEdit(isControlEditableByBillingCode);
        CustInvoiceTemplateLine_AmountCur.allowEdit(isControlEditableByBillingCode);
        CustInvoiceTemplateLine_CustInvoiceStandardLineTemplate_UnitPrice.allowEdit(isControlEditableByBillingCode);
        GroupProject_ProjId.allowEdit(isControlEditableByBillingCode);
        GroupProject_ProjCategoryId.allowEdit(isControlEditableByBillingCode);
    }
}

void setTaxWithholdEnabled_IN()
{
    custInvoiceTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceTemplateTaxExtension_IN,tdsGroup)).allowEdit(!custInvoiceTemplateTaxExtension_IN.tcsGroup);
    custInvoiceTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceTemplateTaxExtension_IN,tcsGroup)).allowEdit(!custInvoiceTemplateTaxExtension_IN.tdsGroup);
    custInvoiceLineTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceLineTemplateTaxExtension_IN,tdsGroup)).allowEdit(!custInvoiceLineTemplateTaxExtension_IN.tcsGroup);
    custInvoiceLineTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceLineTemplateTaxExtension_IN,tcsGroup)).allowEdit(!custInvoiceLineTemplateTaxExtension_IN.tdsGroup);
}

