    header_WithholdingTax_IN.visible(taxWithholdParameters.tdsInvoice || taxWithholdParameters.tcsInvoice);
    custInvoiceTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceTemplateTaxExtension_IN,tdsGroup)).visible(taxWithholdParameters.tdsInvoice);
    custInvoiceTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceTemplateTaxExtension_IN,tcsGroup)).visible(taxWithholdParameters.tcsInvoice);
    custInvoiceLineTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceLineTemplateTaxExtension_IN,tdsGroup)).visible(taxWithholdParameters.tdsInvoice);
    custInvoiceLineTemplateTaxExtension_IN_ds.object(fieldNum(CustInvoiceLineTemplateTaxExtension_IN,tcsGroup)).visible(taxWithholdParameters.tcsInvoice);
}

