/// <summary>
/// Updates the values in the ledger dimension field in the <c>CustInvoiceStandardLineTemplate</c> table based
/// on the current version of billing code.
/// </summary>
private void updateInvoiceLineLedgerDimensionByBillingCodeVersion()
{
    CustInvoiceStandardLineTemplate custInvoiceStandardLineTemplate;

    ttsbegin;
    while select forupdate custInvoiceStandardLineTemplate
        where custInvoiceStandardLineTemplate.CustInvoiceTemplate == custInvoiceTemplate.RecId
            && custInvoiceStandardLineTemplate.BillingCode > 0
    {
        custInvoiceStandardLineTemplate.setLedgerDimensionByBillingCodeVersion();
        custInvoiceStandardLineTemplate.update();
    }
    ttscommit;
}

