/// <summary>
/// The query is executed when a form is opened for data display.
/// </summary>
public void executeQuery()
{
    tmplLine_ds.query().dataSourceName(tmplLine_ds.name()).addRange(
        fieldNum(CustInvoiceLineTemplate, InstanceRelationType)).value(SysQuery::value(DictTable::getRelationTypeFromTableName(tableStr(CustInvoiceStandardLineTemplate))));

    // Update the ledger dimension based on the current version of billing code for all billing code lines.
    element.updateInvoiceLineLedgerDimensionByBillingCodeVersion();

    super();
    tmplLine_ds.insertIfEmpty(true);
}

