/// <summary>
/// Writes a invoice line template record.
/// </summary>
public void write()
{
    boolean isBillingCodeModified;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        isBillingCodeModified = tmplLine_CustInvoiceStandardLineTemplate.orig().BillingCode != tmplLine_CustInvoiceStandardLineTemplate.BillingCode ? true : false;
    }

    super();

    // <GIN>
    if (custInvoiceLineTemplateTaxExtension_IN_ds.isNeeded())
    {
        if (custInvoiceLineTemplateTaxExtension_IN.CustInvoiceLineTemplate == 0)
        {
            custInvoiceLineTemplateTaxExtension_IN.CustInvoiceLineTemplate = TmplLine.RecId;
            if (custInvoiceLineTemplateTaxExtension_IN_ds.validateWrite())
            {
                custInvoiceLineTemplateTaxExtension_IN.write();
            }
        }
    }
    // </GIN>

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        ttsbegin;
        if (tmplLine_CustInvoiceStandardLineTemplate.RecId && tmplLine_CustInvoiceStandardLineTemplate.BillingCode)
        {
            if (isBillingCodeModified)
            {
                tmplLine_CustInvoiceStandardLineTemplate.setDefaultInvoiceDistribution(CustBillingCodeVersion::findByDate(tmplLine_CustInvoiceStandardLineTemplate.BillingCode, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())).RecId);
                CustInvoiceDistributionTemplate::updateDistributionLineAmount(tmplLine);
            }
            CustInvLineBillCodeCustomFieldBase::writeCustCustomFields(mapCustCustomFieldElements,CustCustomFieldSourceType::CustInvoiceTemplateLine,tmplLine_CustInvoiceStandardLineTemplate);
        }
        ttscommit;

        element.updateDesign();
    }
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    CustBillingCode custBillingCode;
    QueryBuildDataSource custBillingCodeQBDS;
    QueryBuildDataSource custBillingClassificationCodeQBDS;
    QueryBuildDataSource custBillingCodeVersionQBDS;
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tablenum(CustBillingCode),
                                                                                    _formReferenceControl, true);
    Query lookupQuery;

    sysTableLookup.addLookupfield(fieldnum(CustBillingCode, BillingCode));
    sysTableLookup.addLookupfield(fieldnum(CustBillingCode, Description));

    lookupQuery = new Query();
    custBillingCodeQBDS = lookupQuery.addDataSource(tablenum(CustBillingCode));

    custBillingClassificationCodeQBDS = custBillingCodeQBDS.addDataSource(tablenum(CustBillingClassificationCode));
    custBillingClassificationCodeQBDS.addLink(fieldnum(CustBillingCode, RecId),
                                                fieldnum(CustBillingClassificationCode, CustBillingCode));
    custBillingClassificationCodeQBDS.addRange(fieldnum(CustBillingClassificationCode,
                                                        CustBillingClassification)).value(SysQuery::value(custInvoiceTemplate.BillingClassification));

    custBillingCodeVersionQBDS = custBillingCodeQBDS.addDataSource(tablenum(CustBillingCodeVersion));
    custBillingCodeVersionQBDS.addLink(fieldnum(CustBillingCode, RecId),
                                        fieldnum(CustBillingCodeVersion, CustBillingCode));
    custBillingCodeVersionQBDS.addRange(fieldnum(CustBillingCodeVersion, ValidFrom)).value(SysQuery::value(strfmt('((%1.%2 <= %3) && (%1.%4 >= %3))',
                                                                                                            custBillingCodeVersionQBDS.name(),
                                                                                                            fieldstr(CustBillingCodeVersion,ValidFrom),
                                                                                                            DateTimeUtil::toStr(DateTimeUtil::utcNow()),
                                                                                                            fieldstr(CustBillingCodeVersion,ValidTo))));

    sysTableLookup.parmQuery(lookupQuery);

    custBillingCode = sysTableLookup.performFormLookup();

    return custBillingCode;
}

public void modified()
{
    CustBillingCodeVersion billingCodeVesrionLocal;
    DialogButton dialogButton = DialogButton::Yes;

    void promptBillingCodeChanged()
    {
        if ( tmplLine_CustInvoiceStandardLineTemplate.BillingCode != 0 && tmplLine_CustInvoiceStandardLineTemplate.BillingCode != tmplLine_CustInvoiceStandardLineTemplate.orig().BillingCode &&
                                            ( tmplLine_CustInvoiceStandardLineTemplate.AmountCur != 0 ||
                                            tmplLine_CustInvoiceStandardLineTemplate.LedgerDimension != 0 || tmplLine_CustInvoiceStandardLineTemplate.TaxItemGroup != '' ||
                                            tmplLine_CustInvoiceStandardLineTemplate.Description != '' ))
        {
            dialogButton = Box::yesNo( "@SPS839", DialogButton::No);
        }
    }

    if (tmplLine_CustInvoiceStandardLineTemplate.BillingCode)
    {
        //Prompts a dialog for confirmation of change in billing code
        promptBillingCodeChanged();
        if (dialogButton == DialogButton::Yes)
        {
            super();

            billingCodeVesrionLocal = CustBillingCodeVersion::findByDate(tmplLine_CustInvoiceStandardLineTemplate.BillingCode, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
            mapCustCustomFieldElements = null;

            if (!element.openCustomField())
            {
                return;
            }

            //Convert currency based on amount defined in Billing Code
            tmplLine_CustInvoiceStandardLineTemplate.convertCurrencyCode(custInvoiceTemplate.orig().CurrencyCode, custInvoiceTemplate.CurrencyCode, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                                CustInvoiceChangeCurrencyOption::ResetToBillingCodeRate);
        }
        else
        {
            tmplLine_CustInvoiceStandardLineTemplate.BillingCode = tmplLine_CustInvoiceStandardLineTemplate.orig().BillingCode;
        }
    }

    //Enable or Disable fields based on billing code
    element.updateDesign();
    tmplLine_CustInvoiceStandardLineTemplate_ds.reread();
    tmplLine_CustInvoiceStandardLineTemplate_ds.refresh();
    element.setFieldAccess();
}

