/// <summary>
/// Called when the dialog associated with the <c>CustInvoiceTemplateCopyDropDialogButton</c>
/// button is closed which adds the new free text invoice template specified in the dialog.
/// </summary>
/// <param name="_formRun">
/// The dialog form which was closed.
/// </param>
public void dialogClosed(xFormRun _formRun)
{
    Object formRunObj;
    CustInvoiceTemplateName custInvoiceTemplateName;
    Description custInvoiceTemplateDescription;

    super(_formRun);

    if (_formRun.closedOk())
    {
        formRunObj = _formRun;
        if (formHasMethod(_formRun, 'parmCustInvoiceTemplateName'))
        {
            custInvoiceTemplateName = formRunObj.parmCustInvoiceTemplateName();
        }
        if (formHasMethod(_formRun, 'parmCustInvoiceTemplateDescription'))
        {
            custInvoiceTemplateDescription = formRunObj.parmCustInvoiceTemplateDescription();
        }

        CustInvoiceTemplate::copyCustInvoiceTemplate(custInvoiceTemplate, custInvoiceTemplateName, custInvoiceTemplateDescription);
        custInvoiceTemplate_ds.research(true);
    }
}

/// <summary>
/// Enables the CustInvoiceDistributionTemplateButton button on page activation based on <c>CustInvoiceLineTemplate</c> record ID.
/// </summary>
public void pageActivated()
{
    boolean showProject;

    super();

    // Controling the visibility of project group
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        showProject = custParameters.EnableProject;
        groupProject.visible(showProject);
        tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, ProjId)).visible(showProject);
        tmplLine_CustInvoiceStandardLineTemplate_ds.object(fieldNum(CustInvoiceStandardLineTemplate, ProjCategoryId)).visible(showProject);
        element.setFieldAccess();
    }
}

void clicked()
{
    if (tmplLine.RecId == 0.0)
    {
        throw error("@SYS136802");
    }

    super();
}

void clicked()
{
    super();

    element.openCustomField();
    tmplLine_DS.write();
}

/// <summary>
/// Perform a custom lookup for the Billing Code
/// </summary>
public void lookup()
{
    QueryBuildDataSource custBillingCodeQBDS;
    QueryBuildDataSource custBillingClassificationCodeQBDS;

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(CustBillingCode), this, true);
    Query lookupQuery;

    sysTableLookup.addLookupfield(fieldNum(CustBillingCode, BillingCode));
    sysTableLookup.addLookupfield(fieldNum(CustBillingCode, Description));

    lookupQuery = new Query();
    custBillingCodeQBDS = lookupQuery.addDataSource(tableNum(CustBillingCode));

    custBillingClassificationCodeQBDS = custBillingCodeQBDS.addDataSource(tableNum(CustBillingClassificationCode));
    custBillingClassificationCodeQBDS.addLink(fieldNum(CustBillingCode, RecId),
                                                fieldNum(CustBillingClassificationCode, CustBillingCode));
    custBillingClassificationCodeQBDS.addRange(fieldNum(CustBillingClassificationCode,
                                                        CustBillingClassification)).value(SysQuery::value(custInvoiceTemplate.BillingClassification));

    sysTableLookup.parmQuery(lookupQuery);

    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean isModifiedSuccessful = true;
    DialogButton dialogButton = DialogButton::Yes;

    void promptBillingCodeChanged()
    {
        if ( tmplLine_CustInvoiceStandardLineTemplate.RecId != 0 && tmplLine_CustInvoiceStandardLineTemplate.BillingCode != 0 && tmplLine_CustInvoiceStandardLineTemplate.BillingCode != tmplLine_CustInvoiceStandardLineTemplate.orig().BillingCode &&
                                            ( tmplLine_CustInvoiceStandardLineTemplate.AmountCur != 0 ||
                                            tmplLine_CustInvoiceStandardLineTemplate.LedgerDimension != 0 || tmplLine_CustInvoiceStandardLineTemplate.TaxItemGroup != '' ||
                                            tmplLine_CustInvoiceStandardLineTemplate.Description != '' ))
        {
            dialogButton = Box::yesNo( "@SPS839", DialogButton::No);
        }
    }

    if (this.text() != '')
    {
        //Prompts a dialog for confirmation of change in billing code
        promptBillingCodeChanged();
        if (dialogButton == DialogButton::Yes)
        {
            mapCustCustomFieldElements = null;
            isModifiedSuccessful = super();

            if (isModifiedSuccessful)
            {
                mapCustCustomFieldElements = null;

                // clear template fields since we have a billing code now
                element.clearFields();

                tmplLine_CustInvoiceStandardLineTemplate.Quantity = 1;
            }
        }
        else
        {
            isModifiedSuccessful = false;
        }
    }
    else
    {
        isModifiedSuccessful = super();
    }

    if (isModifiedSuccessful)
    {
        element.updateDesign();
        element.setFieldAccess();
    }

    return isModifiedSuccessful;
}

