/// <summary>
/// Provides the edit method for control <c>BankDocumentTypeFilter</c>.
/// </summary>
/// <param name="_set">
/// A Boolean value that indicates whether to set or read the value.
/// </param>
/// <param name="_value">
/// The value set from the form control.
/// </param>
/// <returns>
/// The value should be displayed on the form control.
/// </returns>
edit BankLCBankDocument editBankDocumentTypeFilter(
    boolean _set,
    BankLCBankDocument _value)
{
    if (_set)
    {
        bankLCTransHelper.parmBankDocumentTypeFilter(_value);
    }

    return bankLCTransHelper.parmBankDocumentTypeFilter();
}

void init()
{
    SalesTable salesTable;
    CustInvoiceTable custInvoiceTable;

    switch (element.args().dataset())
    {
        case tablenum(CustTable) :
        custTable = element.args().record();
        break;
        case tablenum(SalesTable) :
            salesTable = element.args().record();
            custTable = CustTable::find(salesTable.InvoiceAccount);
        break;
        case tablenum(CustInvoiceTable) :
            custInvoiceTable = element.args().record();
            custTable = CustTable::find(custInvoiceTable.InvoiceAccount);
        break;
    }

    if (!custTable)
    {
        throw error("@SYS22842");
    }

    super();

    // Save the account number the balance is calculated for to make sure to don't calculate again in linkActive
    balanceCalculatedForAccount = custTable.AccountNum;

    element.initBankLCTransHelper();

    // Calculate customer balances
    element.updateBalance();

    if (CustParameters::isBillingClassificationEnabled())
    {
        //calculate balance by billing classification
        custBillingClassificationBalance.setTmpData(TmpCustBillingClassificationBalance::custBillingClassificationBalance(custTable.AccountNum, true));
        BillingClassificationGrid.visible(true);
    }
}

/// <summary>
/// Inits the instance of <c>BankLCCustVendTransHelper</c>.
/// </summary>
private void initBankLCTransHelper()
{
    bankLCTransHelper = BankLCCustTransHelper::construct();
    bankLCTransHelper.parmBankLCCustTransDS(bankLCCustTrans_DS);
    bankLCTransHelper.parmBankDocumentTypeFilterControl(bankDocumentTypeFilter);
    bankLCTransHelper.configBankLCControls(custTrans_DS.query());
}

/// <summary>
/// Calculates the pending on-account amount since it may be in credit workbench.
/// </summary>
/// <returns>
/// Pending amount for a specific customer.
/// </returns>
public display AmountMst MCRPendingOrderAmount()
{
    mcrCustPaymTable mcrCustPaymTable;
    AmountMst pendingAmount;

    while select currencycode, sum(Amount) from mcrCustPaymTable
        group by CurrencyCode
        where mcrCustPaymTable.CustAccount == CustTable.AccountNum
           && mcrCustPaymTable.CustPaymType == MCRCustPaymType::OnAccount
           && mcrCustPaymTable.Status == MCRCustPaymStatus::Declined
    {
        pendingAmount += CurrencyExchangeHelper::amountCur2MST(mcrCustPaymTable.Amount, mcrCustPaymTable.CurrencyCode);
    }
    return pendingAmount;
}

/// <summary>
/// Executes the query to refresh the data.
/// </summary>
public void refreshData()
{
    custTrans_DS.executeQuery();

    element.updateBalance();

    custTransBalanceCurrency_DS.executeQuery();
}

/// <summary>
/// Updates the balance.
/// </summary>
public void updateBalance()
{
    custTransBalanceCurrency.setTmpData(
        TmpCustVendTrans::custTransBalanceCurrency(
            custTable.AccountNum,
            true,
            element.currencyCode(),
            bankLCTransHelper.parmBankDocumentTypeFilter()));
}

void linkActive()
{
    if (custTable.AccountNum)
    {
        custTrans_ds.query().dataSourceTable(tablenum(CustTrans)).findRange(fieldnum(CustTrans, AccountNum)).value(strfmt('%1', custTable.AccountNum));
    }
    else
    {
        element.close();
    }

    super();

    if (!custTrans)
    {
        custTrans_ds.refresh();
    }
}

void init()
{
    Query q = new Query(querystr(CustOpenBalance));

    super();

    custTrans_ds.query(q);
}

public int active()
{
    int ret;

    ret = super();

    if (custTransBalanceCurrency.CurrencyCode != '')
    {
        detailsCustTrans.enabled(true);
    }
    else
    {
        detailsCustTrans.enabled(false);
    }

    return ret;
}

void linkActive()
{;

    if (custTable.AccountNum)
    {
        // Only recalculate if the account number is changed
        if (custTable.AccountNum != balanceCalculatedForAccount)
        {
            // Calculate customer balances
            custTransBalanceCurrency.setTmpData(TmpCustVendTrans::custTransBalanceCurrency(custTable.AccountNum, true, element.currencyCode()));

            balanceCalculatedForAccount = custTable.AccountNum;
        }
    }

    super();
}

display AmountMSTDebCred sumAmount()
{
    return vendTrans.AmountMST;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceTable(tablenum(VendTrans));
    qbds.addSelectionField(fieldnum(VendTrans, AmountMST), SelectionField::Sum);
    qbds.orderMode(OrderMode::GroupBy);
    qbds = qbds.addDataSource(tablenum(VendTable));
    qbds.relations(true);
    qbds.joinMode(JoinMode::ExistsJoin);
    qbds.addDynalink(fieldnum(VendTable, Party), custTable, fieldnum(CustTable, Party));
}

public void clicked()
{
    Args args = new Args();
    MenuFunction menuFunction;
    CustVendTransQueryBuild custVendTransQueryBuild;

    menuFunction = new MenuFunction(this.menuItemName(), this.menuItemType());

    custVendTransQueryBuild = CustVendTransQueryBuild::construct(CustVendTransQueryType::CustTrans);

    CustVendShowTransaction defaultTransactionView = CustVendShowTransaction::Open;
    args.parmEnum(defaultTransactionView);
    args.parmEnumType(enumNum(CustVendShowTransaction));

    custVendTransQueryBuild.parmAccountNum(custTable.AccountNum);
    custVendTransQueryBuild.parmOpenTrans(true);

    args.parmObject(new Query(custVendTransQueryBuild.buildQuery()));

    menuFunction.run(args);
}

