public container determineMandateHandlingUponReversal()
{
    SpecTrans specTrans;
    CustSettlement custSettlement;
    boolean isTransSepa, isOffsetSepa;
    boolean shouldDecrementMandateCount = false;
    boolean shouldCancelReversal = false;
    DialogButton result;

    while select crosscompany SpecTableId, SpecRecId, SpecCompany, RefTableId, RefRecId, RefCompany from specTrans
        where specTrans.SpecTableId == custTableCaller.TableId
           && specTrans.SpecRecId == custTableCaller.RecId
           && specTrans.SpecCompany == custTableCaller.company()
        join TransCompany, TransRecId, OffsetCompany, OffsetRecid from custSettlement
            where custSettlement.TableId == specTrans.RefTableId
               && custSettlement.RecId == specTrans.RefRecId
               && custSettlement.DataAreaId == specTrans.RefCompany
    {
        isTransSepa = CustDirectDebitMandate::isTransactionSepa(custSettlement.TransCompany, custSettlement.TransRecId);
        isOffsetSepa = CustDirectDebitMandate::isTransactionSepa(custSettlement.OffsetCompany, custSettlement.OffsetRecid);

        if (isTransSepa && isOffsetSepa)
        {
            shouldDecrementMandateCount = true;
            break;
        }
    }

    if (shouldDecrementMandateCount)
    {
        result = Box::yesNoCancel("@SYS4002638", DialogButton::Yes);

        if (result == DialogButton::No)
        {
            shouldDecrementMandateCount = false;
        }
        else if (result == DialogButton::Cancel)
        {
            shouldCancelReversal = true;
        }
    }

    return [shouldCancelReversal, shouldDecrementMandateCount];
}

