public edit TransDate editSettlePostingDate(boolean _set, TransDate _settlePostingDate)
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    if (_set)
    {
        parmSettlePostingDate = _settlePostingDate;
        // <GEERU><GEEPL>
        if (countryRegion_RUPL || TaxParameters::find().ReverseSettlementDatePrinciple_W || usePostingLog)
        {
            custReversePosting.parmSaveDate_W(parmSettlePostingDate);
            xSysLastValue::saveLast(custReversePosting);
        }
        // </GEERU></GEEPL>
    }

    return parmSettlePostingDate;
}

public edit SettleDatePrinc editSettlePostingType(boolean _set, SettleDatePrinc _settlePostingType)
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>
    if (_set)
    {
        parmSettlePostingType = _settlePostingType;

        if (parmSettlePostingType == SettleDatePrinc::DateOfPayment)
        {
            this.editSettlePostingDate(true, dateNull());
        }
        else if (parmSettlePostingType == SettleDatePrinc::DaysDate ||
            parmSettlePostingType == SettleDatePrinc::SelectDate)
        {
            this.editSettlePostingDate(true, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()));
        }

        // Enable or disable date control based on type
        if (parmSettlePostingType == SettleDatePrinc::SelectDate)
        {
            settlePostingDate.enabled(true);
        }
        else
        {
            settlePostingDate.enabled(false);
        }
    }
    // <GEERU><GEEPL>
    if (TaxParameters::find().ReverseSettlementDatePrinciple_W || countryRegion_RUPL || usePostingLog)
    {
        custReversePosting.parmDatePrinciple(parmSettlePostingType);
        custReversePosting.parmSaveDate_W(parmSettlePostingDate);
        xSysLastValue::saveLast(custReversePosting);
    }
    // </GEERU></GEEPL>

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        settlePostingDate.enabled(parmSettlePostingType == SettleDatePrinc::SelectDate);
    }
    // </GEERU>

    return parmSettlePostingType;
}

public void init()
{
    Common common = element.args().record();

    super();

    if (common.RecId == 0)
    {
        throw error(Error::missingRecord(this.name()));
    }

    this.initSettlementDateController();

    imageList = new ImageListAppl_CustVend();
    isMarkedByOtherSpec.imageList(imageList.imageList());

    this.updateDataSource();

    element.initBankLCTransHelper();

    MarkTrans.setFocus();

    if (element.args().caller() && element.args().caller().name() == formStr(SettlementView))
    {
        // Settlement view does not maintain a dynalink to trigger linkActive() on the form datasource.
        this.setCallerRecord();
        this.initFromCommon();

        if (element.args().lookupRecord() is CustTrans)
        {
            custTrans_DS.setRecord(element.args().lookupRecord());
        }
    }

    if (element.args().caller() && element.args().caller().name() == formStr(MCRSalesTableOrderHistory) && !specTransManager)
    {
        specTransManager = SpecTransManager::newRefTableId(custTableCaller, tableNum(CustSettlement), true);
    }
}

private void initSettlementDateController()
{
    CustVendOpenTransReverseSettlementDateContract contract = CustVendOpenTransReverseSettlementDateContract::construct();

    Query query = custTrans_ds.query();
    QueryBuildDataSource qbdsCustSettlement = query.dataSourceTable(tableNum(CustSettlement));

    contract.parmSettlementDateRangeGroup(SettlementDateRangeGroup);
    contract.parmFromDate(FromDate);
    contract.parmToDate(ToDate);
    contract.parmSettlementDate(CustSettlement_TransDate);
    contract.parmCustVendSettlementTransDateFieldNum(fieldNum(CustSettlement, TransDate));
    contract.parmCustVendSettlementDataSource(qbdsCustSettlement);

    settlementDateController = CustVendOpenTransReverseSettlementDateController::newFromContract(contract);
    settlementDateController.init();
}

private void runQueryAndEnableFilters(AsyncTaskResult _result)
{
    CustTrans_ds.executeQuery();
    FromDate.enabled(true);
    ToDate.enabled(true);
}

