public void initFromCommon()
{
    // <GEERU>
    #ISOCountryRegionCodes
    // </GEERU>

    // Validate that the calling record exists & is the CustTable
    if (caller.RecId == 0)
    {
        throw error("@SYS22842");
    }
    else if (caller.TableId != tableNum(CustTable))
    {
        throw error(Error::wrongUseOfFunction(this.name()));
    }
    else
    {
        custTableCaller = caller;

        // This gets called from linkActive so this is here to ensure the query range is always up to date.
        qbrAccountNum.value(strFmt('(((%1 == \"%2\") && (%4 == \"%5\")) || ((%3 == \"%2\") && (%6 == \"%5\")))',
            fieldStr(CustSettlement, AccountNum),
            SysQuery::value(custTableCaller.AccountNum),
            fieldStr(CustSettlement, OffsetAccountNum),
            fieldStr(CustSettlement, TransCompany),
            SysQuery::value(custTableCaller.company()),
            fieldStr(CustSettlement, OffsetCompany)));
        qbrAccountNum.status(RangeStatus::Hidden);

        if (custTransRecId)
        {
            qbrTransRecId.value(strFmt('((%1 == %2) || (%3 == %2))',
            fieldStr(CustSettlement, TransRecId),
            SysQuery::value(custTransRecId),
            fieldStr(CustSettlement, OffsetRecId)));
            qbrTransRecId.status(RangeStatus::Hidden);
        }
        else
        {
            settlementDateController.applyRange();
        }

        // <GEERU><GEEPL>
        if (TaxParameters::find().ReverseSettlementDatePrinciple_W || countryRegion_RUPL || usePostingLog)
        {
            custReversePosting    = CustVendReversePosting::construct(custTableCaller, null);
            parmSettlePostingType = custReversePosting.parmDatePrinciple();
            parmSettlePostingDate = custReversePosting.parmSaveDate_W();
        }
        // </GEERU></GEEPL>
    }

    specTransManager = SpecTransManager::newRefTableId(custTableCaller, tableNum(CustSettlement), true);
}

public void updateDataSource()
{
    Query formQuery;
    QueryBuildDataSource qbdsCustTrans;
    QueryBuildDataSource qbdsCustSettlement;
    formQuery = custTrans_ds.query();

    // Clear dynalinks for the CustTrans data source so that the caller's dataAreaId doesn't restrict the query
    qbdsCustTrans = formQuery.dataSourceTable(tableNum(CustTrans));
    qbdsCustTrans.clearDynalinks();

    // Add range on CanBeReversed flag
    qbdsCustSettlement = formQuery.dataSourceTable(tableNum(CustSettlement));
    criteriaAllNew = qbdsCustSettlement.addRange(fieldNum(CustSettlement, CanBeReversed));

    // Add range to include all accounts from the shared service boundary
    qbrAccountNum = qbdsCustSettlement.addRange(fieldNum(CustSettlement, AccountNum));
    qbrTransRecId = qbdsCustSettlement.addRange(fieldNum(CustSettlement, TransRecId));

    settlementDateController.addRange();
    settlementDateController.addSortField();

    // SpecTransMarked is only used for filtering marked records
    this.setSpecTransDS(false);
}

public boolean validateSettlePostingDate(SettleDatePrinc _settlePostingType, TransDate _settlePostingDate)
{
    boolean isValid = true;

    if (_settlePostingDate != dateNull())
    {
        isValid = custSettlement1.custVendSettlementInterface().validateReversalSettlementDate(custTableCaller, _settlePostingDate);
    }
    // The settlement posting date cannot be blank if the type is selected date
    else if (_settlePostingType == SettleDatePrinc::SelectDate)
    {
        isValid = checkFailed("@SYS117112");
    }

    return isValid;
}

