public int active()
{
    int ret;
    CustInvoiceTable custInvoiceTable;

    ret = super();

    custTransDetails.setCustVendTrans(custTrans);

    if ((custTrans.Invoice) && (custTrans.TransType == LedgerTransType::Cust))
    {
        custInvoiceTable = CustInvoiceJour::findFromCustTrans(
            custTrans.Invoice,
            custTrans.TransDate,
            custTrans.AccountNum).custInvoiceTable();
    }

    // enable/disable reverse payment button if invoice is parent or adjusting
    if (CustInterestAdjustmentHistory::doesAdjustedInvoiceExist(custInvoiceTable.RecId))
    {
        reverseNew.enabled(false);
    }

    // Get the dispute information for the transaction
    disputeHelper = CustVendDisputeHelper::construct(custTrans.company(), tableNum(CustTrans), custTrans.RecId);

    return ret;
}

