public edit NoYes markTrans(boolean _set, CustVendSettlement _custSettlement, NoYes _markTrans)
{
    if (_set)
    {
        if (_markTrans == NoYes::Yes)
        {
            // Enable/disable reverse payment button if invoice is parent or adjusting
            if (custTrans.isParentOrAdjustingInvoice())
            {
                error("@SYS302654");
                reverseNew.enabled(false);
                return false;
            }
            else
            {
                reverseNew.enabled(true);
            }

            if (CustTrans::find(_custSettlement.TransRecId).BillOfExchangeID || CustTrans::find(_custSettlement.OffsetRecId).BillOfExchangeID)
            {
                error("@CashManagement:BillOfExchangeTransactionReverse");
                reverseNew.enabled(false);
                return false;
            }

            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                if (!specTransManager.existForOtherSpec(_custSettlement.DataAreaId, _custSettlement.TableId, _custSettlement.RecId)
                    && !CustOverPaym::isReimbursementSettlement(_custSettlement))
                {
                    _custSettlement.CustVendSettlement::markThisAndRelatedOffsets(specTransManager, custTrans.CurrencyCode);
                }
            }
            else if (!specTransManager.existForOtherSpec(_custSettlement.DataAreaId, _custSettlement.TableId, _custSettlement.RecId))
            {
                _custSettlement.CustVendSettlement::markThisAndRelatedOffsets(specTransManager, custTrans.CurrencyCode);
                // <GTH>
                if (TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled())
                {
                    _custSettlement.CustVendSettlement::markRelatedTaxWithholdVoucher(
                        specTransManager,
                        custTrans.Voucher,
                        custTrans.TransDate);
                }
                // </GTH>
            }
            else
            {
                error("@SYS117108");
            }
        }
        else
        {
            _custSettlement.CustVendSettlement::unmarkThisAndRelatedOffsets(specTransManager);
            // <GTH>
            if (TaxThaiGovCertificationFeatureChecker::isTaxWithholdEnabled())
            {
                _custSettlement.custVendSettlement::unmarkRelatedTaxWithholdVoucher(
                    specTransManager,
                    custTrans.Voucher,
                    custTrans.TransDate);
            }
            // </GTH>

            if (specTransManager.getSpecTransCount() < 1)
            {
                reverseNew.enabled(false);
            }
        }

        custTrans_ds.refreshEx(-1);
    }

    return specTransManager.exist(_custSettlement.DataAreaId, _custSettlement.TableId, _custSettlement.RecId);
}

