public void clicked()
{
    boolean shouldDecrementMandateCount;
    boolean shouldCancelReversal;

    super();

    if (element.validateSettlePostingDate(parmSettlePostingType, parmSettlePostingDate)
        && !CustTrans::hasLCRelatedTransToBeReversed(custTableCaller))
    {
        [shouldCancelReversal, shouldDecrementMandateCount] = element.determineMandateHandlingUponReversal();

        if (!shouldCancelReversal
            && CustTrans::reverseTransact(custTableCaller, null, parmSettlePostingType, parmSettlePostingDate, true, '', shouldDecrementMandateCount))
        {
            specTransManager.deleteAll();
            custTrans_ds.executeQuery();
        }
    }

    element.close();
}

public void clicked()
{
    super();

    specTransManager.deleteAll();
    element.close();
}
