void setFilterFieldAppearance(SettlementFilterType_RU   _settlementFilterType_RU,
                              FormStringControl         _cnt1,
                              FormStringControl         _companyCtrl)
{
    ;

    _cnt1.text('');

    switch (_settlementFilterType_RU)
    {
        case SettlementFilterType_RU::Agreement :
            //Lookup for Agreement

            _cnt1.label(literalstr("@GLS220539"));
            _cnt1.enabled(true);

            _companyCtrl.visible(true);
            break;

        case SettlementFilterType_RU::Invoice :
            //Lookup for Invoice

            _cnt1.label(literalstr("@GLS104802"));
            _cnt1.enabled(true);

            _companyCtrl.visible(false);
            break;

        default :
            _cnt1.enabled(false);
            _companyCtrl.visible(false);
    }
}

