public boolean unpack(container packedClass)
{
    Integer     version     = conpeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion:
            [version,#CurrentList] = packedClass;
            break;
        default:
            return false;
    }

    return true;
}

public void updateDataSourceDynamic()
{
    container   companies;
    int         i;

    void setCompanyRange(Query _query)
    {
        _query.clearCompanyRange();

        for (i = 1; i <= conlen(companies); i++)
        {
            _query.addCompanyRange(conpeek(companies, i));
        }
    }
    ;

    if (isConfigurationkeyEnabled(configurationkeynum(LedgerAdvIntercompanyAccounting)))
    {
        companies = manager.parmSharedServiceCompanies();

        setCompanyRange(debitCustTransOpen_ds.query());
        setCompanyRange(creditCustTransOpen_ds.query());

        qbrPartyDebit.value(queryValue(manager.getParty()));
        qbrPartyCredit.value(queryValue(manager.getParty()));
    }
}

public void updateDataSourceStatic()
{
    QueryBuildDataSource    qbdsCustTransOpen;
    QueryBuildDataSource    qbdsCustTrans;
    QueryBuildDataSource    qbdsCustTable;

    QueryBuildRange getPartyRange(Query _query)
    {
        qbdsCustTransOpen = _query.dataSourceTable(tablenum(CustTransOpen));
        qbdsCustTransOpen.clearDynalinks();

        qbdsCustTrans = _query.dataSourceTable(tablenum(CustTrans));
        qbdsCustTable = qbdsCustTrans.addDataSource(tablenum(CustTable));

        qbdsCustTable.relations(true);
        qbdsCustTable.joinMode(JoinMode::InnerJoin);

        qbdsCustTable.fetchMode(0);

        qbdsCustTable.addSelectionField(fieldnum(CustTable, Party));

        return qbdsCustTable.addRange(fieldnum(CustTable, Party));
    }
    ;

    if (isConfigurationkeyEnabled(configurationkeynum(LedgerAdvIntercompanyAccounting)))
    {
        qbrPartyDebit  = getPartyRange(debitCustTransOpen_ds.query());
        qbrPartyCredit = getPartyRange(creditCustTransOpen_ds.query());
    }
}

/// <summary>
/// Controls the access of <c>MarkTransactionsButton</c> button based on some particular selections on the form.
/// </summary>
void updateMarkTransactionsButton()
{
    CustTransOpen           primaryPayment;

    if (CustParameters::findByCompany(loggedinCompany).UseSettlementPriority == NoYes::No)
    {
        markTransactionsButton.visible(false);
        return;
    }

    if (custSettlePriorityOpenTrans != null)
    {
        primaryPayment = custSettlePriorityOpenTrans.getPrimaryPayment();

        // Enable/Disables the 'Mark Transactions' Button based on the
        if (primaryPayment.RecId != 0        &&
            primaryPayment.AmountCur < 0 )
        {
            markTransactionsButton.enabled(true);
        }
        else
        {
            markTransactionsButton.enabled(false);
        }
    }
}

void updateParty()
{
    ;
    custTable = element.args().record();

    if (manager.getParty() != custTable.Party)
    {
        element.createCustVendOpenTransManager();
        element.updateDataSourceDynamic();

        element.existSpecTrans();

        custVendOpenTransEngine.linkActive(settlementFilterDebitType, settlementFilterCreditType);

        element.refreshRemainAmounts();
    }
}

void visibleCreditInvoiceAmountForTrans()
{
    if (creditInvoiceAmountForTrans.visible())
    {
        creditInvoiceAmountForTrans.setFocus();
        creditInvoiceAmountForTrans.visible(settlementFilterCreditType == SettlementFilterType_RU::Invoice);
        if (gridCredit.controlCount())
        {
            gridCredit.controlNum(1).setFocus();
        }
    }
    else
    {
        creditInvoiceAmountForTrans.visible(settlementFilterCreditType == SettlementFilterType_RU::Invoice);
    }
}

void visibleDebitInvoiceAmountForTrans()
{
    if (debitInvoiceAmountForTrans.visible())
    {
        debitInvoiceAmountForTrans.setFocus();
        debitInvoiceAmountForTrans.visible(settlementFilterDebitType == SettlementFilterType_RU::Invoice);
        if (gridDebit.controlCount())
        {
            gridDebit.controlNum(1).setFocus();
        }
    }
    else
    {
        debitInvoiceAmountForTrans.visible(settlementFilterDebitType == SettlementFilterType_RU::Invoice);
    }
}

