    ret = super();

    if (manager.getTransMarkedByOtherSpec(DebitCustTransOpen))
    {
        markInvoiceLines.enabled(false);
    }
    else
    {
        markInvoiceLines.enabled(CustOpenTransLineManager::setMarkInvoiceButton(element.makeArgs()));
