edit NoYes markPayment(boolean _set, CustTransOpen _custTransOpen, NoYes _markTrans)
{
    NoYes retNoYes;
    ;

    try
    {
        retNoYes = custVendOpenTransEngine.markTransDebit(_set, _custTransOpen, _markTrans);

        if (_set)
        {
            element.refreshRemainAmounts();

            // Enable/Disable 'Mark transactions' button
            element.updateMarkTransactionsButton();
            if (manager.getTransMarkedByOtherSpec(_custTransOpen))
            {
                markInvoiceLines.enabled(false);
            }
            else
            {
                markInvoiceLines.enabled(CustOpenTransLineManager::setMarkInvoiceButton(element.makeArgs()));
            }
        }

        return retNoYes;
    }
    catch(Exception::Error)
    {
        return NoYes::No;
    }
}

