{
    CrossExchRate returnCrossRate;
    ;

    if (_set)
    {
        manager.updateCrossRate(_custTransOpen, _crossRate);
    }

    returnCrossRate = manager.getCrossRate(_custTransOpen);

    return returnCrossRate;
}

display Amount invoiceAmountForTrans(CustTransOpen _custTransOpen)
{
    Amount      amount;

    ;

    if (_custTransOpen.RecId)
    {
        amount  = custVendOpenTransEngine.invoiceAmountForTrans(_custTransOpen.RefRecId, filterDebitValue.text());
    }
    amount = abs(amount);

    //Correct sign returned value
    if ((debitCustTrans.AmountMST < 0) || (debitCustTrans.AmountCur < 0))
        amount = -amount;

    return amount;
}

