public void write()
{
    CustTransCashDisc   custTransCashDiscLocal;

    ;

    super();

    if (!debitCustTransOpen.PossibleCashDisc)
    {
        custTransCashDiscLocal = CustTransCashDisc::findRef(debitCustTransOpen.TableId, debitCustTransOpen.RecId);
        if (custTransCashDiscLocal.RecId)
        {
            debitCustTransOpen.PossibleCashDisc = custTransCashDiscLocal.CashDiscAmount;
            debitCustTransOpen.CashDiscDate     = custTransCashDiscLocal.CashDiscdate;
            debitCustTransOpen.update();
        }
    }
}

