void clicked()
{
    super();

    if (! manager.validateSettlePostingDate(saveDatePrinciple, saveDate))
    {
        return;
    }

    CashManagementEventSource eventSource = CashManagementEventSource::Log;
    eventSource.EventWriteCustOpenTransRUUpdateNowCalled();

    if (manager.settleMarkedTrans())
    {
        custVendOpenTransEngine.updateNow();
        element.transResearch();   //Research datasources
    }
}

public void clicked()
{
    super();

    if (manager.getMarkedPrimaryPayment(CreditCustTransOpen) == NoYes::Yes)
    {
        // Remove primary designation only - does not unmark transaction
        manager.updateMarkedPrimaryPayment(CreditCustTransOpen, NoYes::No);
    }
    else
    {
        if (SpecTrans::existMarkedPayment(custTable.company(), custTable.TableId, custTable.RecId))
        {
            throw error("@SYS67734");
        }
        if (CreditCustTransOpen.RecId)
        {
            if (manager.getTransMarked(CreditCustTransOpen) == NoYes::No)
            {
                // Mark transaction for settlement
                custVendOpenTransEngine.markTransCredit(true, CreditCustTransOpen, NoYes::Yes);
            }

            // Set as primary payment
            manager.updateMarkedPrimaryPayment(CreditCustTransOpen, NoYes::Yes);
        }
    }

    element.refreshRemainAmounts();

    CreditCustTransOpen_ds.refresh();

    // Enable/Disable 'Mark transactions' button
    element.updateMarkTransactionsButton();
}

void clicked()
{
    Args args;
    ;

    args = new Args(formStr(CustOpenTrans_RU));
    args.caller(element);
    args.parmObject(manager);
    args.record(DebitCustTransOpen);
    args.parmEnum(tableNum(CustTable));

    new MenuFunction(menuitemDisplayStr(CustPaymEntryByLine), MenuItemType::Display).run(args);

    DebitCustTransOpen_ds.settle(true, DebitCustTransOpen, manager.getSettleAmount(DebitCustTransOpen));
}

void clicked()
{
    DialogButton markTransDialogButton;

    if (custSettlePriorityOpenTrans.isAlreadyMarked())
    {
        // ask the user if he wants to unmark all transactions and do the marking again
        markTransDialogButton = Box::yesNo("@SYS135033", DialogButton::Yes);

        // if yes unmark transactions else do nothing and return
        if (markTransDialogButton == DialogButton::Yes)
        {
            custSettlePriorityOpenTrans.unmarkAllTransactions();
            DebitCustTransOpen_ds.executeQuery();
        }
        else
        {
            return;
        }
    }

    if (!custSettlePriorityOpenTrans.markTransactionsByPriority())
    {
        // if no transactions is marked due to any reason, show the infolog and return
        info("@SYS135034");
        return;
    }

    DebitCustTransOpen_ds.executeQuery();

    custVendOpenTransEngine.initRemainAmount();
    element.refreshRemainAmounts();
}

public boolean modified()
{
    ;
    settlementFilterDebitType = this.selection();

    if (settlementFilterDebitType != SettlementFilterType_RU::Invoice)
    {
        element.visibleDebitInvoiceAmountForTrans();
    }

    element.setFilterFieldAppearance(settlementFilterDebitType,
                                     filterDebitValue,
                                     companyDebit);

    custVendOpenTransEngine.setQueryForDebitsDS(settlementFilterDebitType);

    element.refreshRemainAmounts();

    if (settlementFilterDebitType == SettlementFilterType_RU::Invoice)
    {
        element.visibleDebitInvoiceAmountForTrans();
    }

    return true;
}

public void lookup()
{
    ;
    custVendOpenTransEngine.filterLookup(settlementFilterDebitType, this, companyDebit.text());
}

public boolean modified()
{
    ;
    custVendOpenTransEngine.setFilterValue(settlementFilterDebitType,
                                            debitCustTransOpen_ds,
                                            filterDebitValue.text());
    element.refreshRemainAmounts();

    return true;
}

