void close()
{
    super();

    if (!element.closedCancel() && custParameters.CreditMaxCheck != TypeOfCreditmaxCheck::None
        && (custParameters.CreditMaxCheck != creditMaxCheck || (custParameters.CreditLimitCheckSales == NoYes::Yes && creditLimitCheckSales == NoYes::No)))
    {
        CustParameters::runCreditLimitCheck();
        creditLimitCheckSales = custParameters.CreditLimitCheckSales;
        creditMaxCheck = custParameters.CreditMaxCheck;
        element.close();
    }
}

