/// <summary>
/// Updates the <c>CustParameters</c> form design.
/// </summary>
/// <remarks>
/// Calls the <c>setAllowEditCreditLimit</c> method to mark the <c>CreditLimit</c> field as editable when minimum limit is entered in <c>CreditIdentificationRequire</c> field.
/// </remarks>

void updateDesign()
{
    if (custParameters.CustInvoiceRefNum_FI)
    {
        updateInvoiceFinland_InvoiceReferenceNumberFormula_FI.allowEdit(true);

        if (custParameters.InvoiceReferenceNumberFormula_FI == InvoiceReferenceNumberFormulaType_FI::Formula1)
        {
            custParameters_InvoiceReferenceNumberSequenceTable.allowEdit(false);
        }
        else
        {
            custParameters_InvoiceReferenceNumberSequenceTable.allowEdit(true);
        }
    }
    else
    {
        updateInvoiceFinland_InvoiceReferenceNumberFormula_FI.allowEdit(false);
        custParameters_InvoiceReferenceNumberSequenceTable.allowEdit(false);
    }

    // credit card module
    shipCarrierEstShippingCharge_CreditCardEstShippingChargeMax.enabled(
        custParameters.CreditCardEstShippingChargeType == CreditCardEstShippingChargeType::Percent);

    // Enable/Disable the Manage Priority buttons
    element.updateSettlementPriorityButton();
    // <GEERU>
    taxation_RU.allowEdit(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) && CustParameters::taxationAllowEdit_RU());
    // </GEERU>
    // <GEEU>
    custParameters_ds.object(fieldNum(CustParameters, CheckInvoice_W)).allowEdit(custParameters.InvoiceIdEditing_W);
    if (custParameters.InvoiceIdEditing_W)
    {
        GlobalizationInstrumentationHelper::featureRun(GlobalizationConstants::FeatureReferenceEEU00023, funcName());
    }
    // </GEEU>

    this.enableSettleRemainder(CustParameters.SettlementWriteOffEnabled);

    this.enableCollectionWriteOffBaseAmountSalesTax();
}

