void updateSettlementPriorityButton()
{
    buttonManagePriority.enabled(custParameters.UseSettlementPriority);

    if (custParameters.UseSettlementPriority && custParameters.AutoSettle)
    {
        // update Use Priority for Automatic Settlement check box
        custParameters_ds.object(fieldNum(CustParameters, UsePriorityOnAutoSettlement)).allowEdit(true);
    }
    else
    {
        custParameters_ds.object(fieldNum(CustParameters, UsePriorityOnAutoSettlement)).allowEdit(false);
        custParameters.UsePriorityOnAutoSettlement = NoYes::No;
    }
}

